import{_ as t,c as a,o as l,ag as i}from"./chunks/framework.BSOECjnK.js";const m=JSON.parse('{"title":"Chaque image porteuse d’information a-t-elle une alternative textuelle ?","description":"","frontmatter":{"title":"Chaque [image porteuse d’information](/rgaa/glossaire/image-porteuse-d-information) a-t-elle une [alternative textuelle](/rgaa/glossaire/alternative-textuelle-image) ?"},"headers":[],"relativePath":"rgaa/criteres/1-images/1.1/index.md","filePath":"rgaa/criteres/1-images/1.1/index.md"}'),o={name:"rgaa/criteres/1-images/1.1/index.md"};function r(n,e,s,u,d,c){return l(),a("div",null,[...e[0]||(e[0]=[i('<h1 id="chaque-image-porteuse-d-information-a-t-elle-une-alternative-textuelle" tabindex="-1">Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">image porteuse d’information</a> a-t-elle une <a href="/eslint-plugin-rgaa/rgaa/glossaire/alternative-textuelle-image.html">alternative textuelle</a> ? <a class="header-anchor" href="#chaque-image-porteuse-d-information-a-t-elle-une-alternative-textuelle" aria-label="Permalink to &quot;Chaque [image porteuse d’information](/rgaa/glossaire/image-porteuse-d-information) a-t-elle une [alternative textuelle](/rgaa/glossaire/alternative-textuelle-image) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque image (balise <code>&lt;img&gt;</code> ou balise possédant l’attribut WAI-ARIA <code>role=&quot;img&quot;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a> a-t-elle une <a href="/eslint-plugin-rgaa/rgaa/glossaire/alternative-textuelle-image.html">alternative textuelle</a> ?</strong></p><ol><li>Retrouver dans le document les images structurées au moyen d’un élément <code>&lt;img&gt;</code> ou d’un élément possédant l’attribut WAI-ARIA <code>role=&quot;img&quot;</code> ;</li><li>Pour chaque image, déterminer si l’image est porteuse d’information ;</li><li>Dans le cas où il s’agit d’un élément <code>&lt;img&gt;</code>, vérifier que l’image est pourvue au moins d’une alternative textuelle parmi les suivantes : <ul><li>Passage de texte associé via l’attribut WAI-ARIA <code>aria-labelledby</code> ;</li><li>Contenu de l’attribut WAI-ARIA <code>aria-label</code> ;</li><li>Contenu de l’attribut <code>alt</code> ;</li><li>Contenu de l’attribut <code>title</code>.</li></ul></li><li>Dans le cas où il s’agit d’un élément possédant l’attribut WAI-ARIA <code>role=&quot;img&quot;</code>, vérifier que l’image est pourvue au moins d’une alternative textuelle parmi les suivantes : <ul><li>Passage de texte associé via l’attribut WAI-ARIA <code>aria-labelledby</code> ;</li><li>Contenu de l’attribut WAI-ARIA <code>aria-label</code>.</li></ul></li><li>Si au moins une alternative textuelle est trouvée, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/zone-d-une-image-reactive.html">zone</a> d’une <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-reactive.html">image réactive</a> (balise <code>&lt;area&gt;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a> a-t-elle une <a href="/eslint-plugin-rgaa/rgaa/glossaire/alternative-textuelle-image.html">alternative textuelle</a> ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;area&gt;</code> ;</li><li>Pour chaque élément <code>&lt;area&gt;</code>, déterminer si la zone réactive est porteuse d’information ;</li><li>Vérifier que la zone réactive est pourvue au moins d’une alternative textuelle parmi les suivantes : <ul><li>Contenu de l’attribut WAI-ARIA <code>aria-label</code> ;</li><li>Contenu de l’attribut <code>alt</code> ;</li></ul></li><li>Si au moins une alternative textuelle est trouvée, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Chaque bouton de type <code>image</code> (balise <code>&lt;input&gt;</code> avec l’attribut <code>type=&quot;image&quot;</code>) a-t-il une <a href="/eslint-plugin-rgaa/rgaa/glossaire/alternative-textuelle-image.html">alternative textuelle</a> ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;input&gt;</code> pourvus de l’attribut <code>type=&quot;image&quot;</code> ;</li><li>Pour chaque élément <code>&lt;input&gt;</code> pourvu de l’attribut type=&quot;image&quot;, déterminer si l’image utilisée est porteuse d’information ;</li><li>Vérifier que l’élément <code>&lt;input&gt;</code> est pourvu au moins d’une alternative textuelle parmi les suivantes : <ul><li>Passage de texte associé via l’attribut WAI-ARIA <code>aria-labelledby</code> ;</li><li>Contenu de l’attribut WAI-ARIA <code>aria-label</code> ;</li><li>Contenu de l’attribut <code>alt</code> ;</li><li>Contenu de l’attribut <code>title</code>.</li></ul></li><li>Si au moins une alternative textuelle est trouvée, <strong>le test est validé</strong>.</li></ol><h3 id="test-4" tabindex="-1">Test 4 <a class="header-anchor" href="#test-4" aria-label="Permalink to &quot;Test 4&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/zone-cliquable.html">zone cliquable</a> d’une image réactive côté serveur est-elle doublée d’un mécanisme utilisable quel que soit le dispositif de pointage utilisé et permettant d’accéder à la même destination ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;img&gt;</code> pourvus de l’attribut <code>ismap</code> ;</li><li>Pour chaque élément <code>&lt;img&gt;</code> pourvu de l’attribut <code>ismap</code>, vérifier la présence d’un lien ou d’un ensemble de liens (ou bien d’un autre type de composant d’interface qui jouerait un rôle similaire comme une liste de sélection, par exemple) permettant d’accéder aux mêmes ressources que lorsque l’image fait l’objet d’un clic.</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-5" tabindex="-1">Test 5 <a class="header-anchor" href="#test-5" aria-label="Permalink to &quot;Test 5&quot;">​</a></h3><p><strong>Chaque image vectorielle (balise <code>&lt;svg&gt;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, vérifie-t-elle ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;svg&gt;</code> ;</li><li>Pour chaque élément <code>&lt;svg&gt;</code>, déterminer si l’image est porteuse d’information ;</li><li>S’assurer que l’élément <code>&lt;svg&gt;</code> est pourvu d’un attribut WAI-ARIA <code>role=&quot;img&quot;</code> ;</li><li>Si ce n’est pas le cas, le test est invalidé.</li><li>Le cas échéant, vérifier que l’élément <code>&lt;svg&gt;</code> est pourvu au moins d’une alternative textuelle parmi les suivantes : <ul><li>Contenu de l’élément <code>&lt;title&gt;</code> ;</li><li>Passage de texte associé via l’attribut WAI-ARIA <code>aria-labelledby</code> ;</li><li>Contenu de l’attribut WAI-ARIA <code>aria-label</code> ;</li></ul></li><li>Si au moins une alternative textuelle est trouvée, <strong>le test est validé</strong>.</li></ol><h3 id="test-6" tabindex="-1">Test 6 <a class="header-anchor" href="#test-6" aria-label="Permalink to &quot;Test 6&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-objet.html">image objet</a> (balise <code>&lt;object&gt;</code> avec l’attribut <code>type=&quot;image/…&quot;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les balises ouvrantes <code>&lt;object&gt;</code> pourvues de l&#39;attribut <code>type=&quot;image/…&quot;</code> ;</li><li>Pour chaque balise ouvrante <code>&lt;object&gt;</code> pourvue de l&#39;attribut <code>type=&quot;image/…&quot;</code>, déterminer si l’image utilisée est porteuse d&#39;information ;</li><li>Vérifier que l’élément <code>&lt;object&gt;</code> est pourvu d’un attribut WAI-ARIA <code>role=&quot;img&quot;</code> ;</li><li>Vérifier que l’élément <code>&lt;object&gt;</code> est pourvu au moins d’une alternative textuelle parmi les suivantes : <ul><li>Passage de texte associé via l’attribut WAI-ARIA <code>aria-labelledby</code> ;</li><li>Contenu de l’attribut WAI-ARIA <code>aria-label</code> ;</li><li>Contenu de l’attribut <code>title</code>.</li></ul></li><li>Si au moins une alternative textuelle est trouvée, <strong>le test est validé</strong> ;</li><li>Sinon, vérifier que l&#39;élément <code>&lt;object&gt;</code> est : <ul><li>Soit immédiatement suivi d&#39;un lien ou bouton adjacent permettant d&#39;accéder à un contenu alternatif ;</li><li>Soit un mécanisme permet à l&#39;utilisateur de remplacer l&#39;élément <code>&lt;object&gt;</code> par un contenu alternatif.</li></ul></li><li>Si c&#39;est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-7" tabindex="-1">Test 7 <a class="header-anchor" href="#test-7" aria-label="Permalink to &quot;Test 7&quot;">​</a></h3><p><strong>Chaque image embarquée (balise <code>&lt;embed&gt;</code> avec l’attribut <code>type=&quot;image/…&quot;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Pour chaque élément <code>&lt;embed&gt;</code> pourvu de l’attribut <code>type=&quot;image/…&quot;</code>, déterminer si l’image utilisée est porteuse d’information ;</li><li>Vérifier que l’élément <code>&lt;embed&gt;</code> est pourvu d’un attribut WAI-ARIA <code>role=&quot;img&quot;</code> ;</li><li>Vérifier que l’élément <code>&lt;embed&gt;</code> est pourvu au moins d’une alternative textuelle parmi les suivantes : <ul><li>Passage de texte associé via l’attribut WAI-ARIA <code>aria-labelledby</code> ;</li><li>Contenu de l’attribut WAI-ARIA <code>aria-label</code> ;</li><li>Contenu de l’attribut <code>title</code>.</li></ul></li><li>Si au moins une alternative textuelle est trouvée, <strong>le test est validé</strong> ;</li><li>Sinon, vérifier que l’élément <code>&lt;embed&gt;</code> est : <ul><li>Soit immédiatement suivi d’un lien ou bouton adjacent permettant d’accéder à un contenu alternatif ;</li><li>Soit un mécanisme permet à l’utilisateur de remplacer l’élément <code>&lt;embed&gt;</code> par un contenu alternatif.</li></ul></li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-8" tabindex="-1">Test 8 <a class="header-anchor" href="#test-8" aria-label="Permalink to &quot;Test 8&quot;">​</a></h3><p><strong>Chaque image bitmap (balise <code>&lt;canvas&gt;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;canvas&gt;</code> ;</li><li>Pour chaque élément <code>&lt;canvas&gt;</code>, déterminer si l’image utilisée est porteuse d’information ;</li><li>Vérifier que l’élément <code>&lt;canvas&gt;</code> est pourvu d’un attribut WAI-ARIA <code>role=&quot;img&quot;</code> ;</li><li>Vérifier que la balise ouvrante <code>&lt;canvas&gt;</code> est pourvue au moins d’une alternative textuelle parmi les suivantes : <ul><li>Passage de texte associé via l’attribut WAI-ARIA <code>aria-labelledby</code> ;</li><li>Contenu de l’attribut WAI-ARIA <code>aria-label</code>.</li></ul></li><li>Si au moins une alternative textuelle est trouvée, <strong>le test est validé</strong>.</li><li>Si les étapes 3 et 4 ne sont pas satisfaites, vérifier que l’élément <code>&lt;canvas&gt;</code> est : <ul><li>Soit pourvu d’un contenu alternatif présent entre les balises <code>&lt;canvas&gt;</code> et <code>&lt;/canvas&gt;</code> ;</li><li>Soit immédiatement suivi d’un lien ou bouton adjacent permettant d’accéder à un contenu alternatif ;</li><li>Soit un mécanisme permet à l’utilisateur de remplacer l’élément <code>&lt;canvas&gt;</code> par un contenu alternatif.</li></ul></li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><p>Note : si l&#39;élément <code>&lt;canvas&gt;</code> dispose d&#39;un rôle <code>img</code>, son alternative ne peut être fournie que par les techniques listées à l&#39;étape 4.</p><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-1-1" tabindex="-1">Critère WCAG 1.1.1 <a class="header-anchor" href="#critere-wcag-1-1-1" aria-label="Permalink to &quot;Critère WCAG 1.1.1&quot;">​</a></h3><p><strong>Non-text Content</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>H36</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H36" target="_blank" rel="noreferrer">Technique H36</a></li><li><strong>H37</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H37" target="_blank" rel="noreferrer">Technique H37</a></li><li><strong>H53</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H53" target="_blank" rel="noreferrer">Technique H53</a></li><li><strong>F65</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F65" target="_blank" rel="noreferrer">Technique F65</a></li><li><strong>H24</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H24" target="_blank" rel="noreferrer">Technique H24</a></li></ul>',33)])])}const p=t(o,[["render",r]]);export{m as __pageData,p as default};
