import{_ as t,c as a,o as i,ag as l}from"./chunks/framework.BSOECjnK.js";const p=JSON.parse('{"title":"Chaque image porteuse d’information a-t-elle, si nécessaire, une description détaillée ?","description":"","frontmatter":{"title":"Chaque image [porteuse d’information](/rgaa/glossaire/image-porteuse-d-information) a-t-elle, si nécessaire, une [description détaillée](/rgaa/glossaire/description-detaillee-image) ?"},"headers":[],"relativePath":"rgaa/criteres/1-images/1.6/index.md","filePath":"rgaa/criteres/1-images/1.6/index.md"}'),o={name:"rgaa/criteres/1-images/1.6/index.md"};function r(n,e,s,d,c,u){return i(),a("div",null,[...e[0]||(e[0]=[l('<h1 id="chaque-image-porteuse-d-information-a-t-elle-si-necessaire-une-description-detaillee" tabindex="-1">Chaque image <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a> a-t-elle, si nécessaire, une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a> ? <a class="header-anchor" href="#chaque-image-porteuse-d-information-a-t-elle-si-necessaire-une-description-detaillee" aria-label="Permalink to &quot;Chaque image [porteuse d’information](/rgaa/glossaire/image-porteuse-d-information) a-t-elle, si nécessaire, une [description détaillée](/rgaa/glossaire/description-detaillee-image) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque image (balise <code>&lt;img&gt;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, qui nécessite une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a>, vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les images structurées au moyen d’un élément <code>&lt;img&gt;</code> (ou d’un élément possédant l’attribut WAI-ARIA <code>role=&quot;img&quot;</code>) porteuses d’information qui nécessitent une description détaillée ;</li><li>Pour chaque image, vérifier qu’il existe : <ul><li>Soit un attribut longdesc qui donne l’adresse (url) d’une page ou d’un emplacement dans la page contenant la description détaillée ;</li><li>Soit une alternative textuelle contenant la référence à une description détaillée adjacente à l’image ;</li><li>Soit un lien ou un bouton adjacent permettant d’accéder à la description détaillée.</li></ul></li><li>Si c’est le cas pour chaque image, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-objet.html">image objet</a> (balise <code>&lt;object&gt;</code> avec l’attribut <code>type=&quot;image/…&quot;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, qui nécessite une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a>, vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;object&gt;</code> pourvus de l’attribut <code>type=&quot;image/…&quot;</code>, porteurs d’information qui nécessitent une description détaillée ;</li><li>Pour chaque élément <code>&lt;object&gt;</code> pourvu de l’attribut <code>type=&quot;image/…&quot;</code>, vérifier qu’il existe : <ul><li>Soit une alternative textuelle contenant la référence à une description détaillée adjacente à l’image ;</li><li>Soit un lien ou un bouton adjacent permettant d’accéder à la description détaillée.</li></ul></li><li>Si c’est le cas pour chaque élément <code>&lt;object&gt;</code> pourvu de l’attribut <code>type=&quot;image/…&quot;</code>, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Chaque image embarquée (balise <code>&lt;embed&gt;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, qui nécessite une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a>, vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;embed&gt;</code> pourvus de l’attribut <code>type=&quot;image/…&quot;</code>, porteurs d’information qui nécessitent une description détaillée ;</li><li>Pour chaque élément <code>&lt;embed&gt;</code> pourvu de l’attribut <code>type=&quot;image/…&quot;</code>, vérifier qu’il existe : <ul><li>Soit une alternative textuelle contenant la référence à une description détaillée adjacente à l’image ;</li><li>Soit un lien ou un bouton adjacent permettant d’accéder à la description détaillée.</li></ul></li><li>Si c’est le cas pour chaque élément <code>&lt;embed&gt;</code> pourvu de l’attribut <code>type=&quot;image/…&quot;</code>, <strong>le test est validé</strong>.</li></ol><h3 id="test-4" tabindex="-1">Test 4 <a class="header-anchor" href="#test-4" aria-label="Permalink to &quot;Test 4&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/bouton-formulaire.html">bouton</a> de type image (balise <code>&lt;input&gt;</code> avec l’attribut <code>type=&quot;image&quot;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteur d’information</a>, qui nécessite une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a>, vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;input&gt;</code> pourvus de l’attribut <code>type=&quot;image&quot;</code>, porteurs d’information qui nécessitent une description détaillée ;</li><li>Pour chaque élément <code>&lt;input&gt;</code> pourvu de l’attribut <code>type=&quot;image&quot;</code>, vérifier qu’il existe : <ul><li>Soit une alternative textuelle contenant la référence à une description détaillée adjacente à l’image ;</li><li>Soit un lien ou un bouton adjacent permettant d’accéder à la description détaillée ;</li><li>Soit un attribut WAI-ARIA aria-describedby associant un passage de texte faisant office de description détaillée.</li></ul></li><li>Si c’est le cas pour chaque élément <code>&lt;input&gt;</code> pourvu de l’attribut <code>type=&quot;image&quot;</code>, <strong>le test est validé</strong>.</li></ol><h3 id="test-5" tabindex="-1">Test 5 <a class="header-anchor" href="#test-5" aria-label="Permalink to &quot;Test 5&quot;">​</a></h3><p><strong>Chaque image vectorielle (balise <code>&lt;svg&gt;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, qui nécessite une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a>, vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;svg&gt;</code> porteurs d’information qui nécessitent une description détaillée ;</li><li>Pour chaque élément <code>&lt;svg&gt;</code>, vérifier qu’il existe : <ul><li>Soit un attribut WAI-ARIA <code>aria-label</code> contenant l’alternative textuelle et une référence à une description détaillée adjacente ;</li><li>Soit un attribut WAI-ARIA <code>aria-labelledby</code> associant un passage de texte faisant office d’alternative textuelle et un autre faisant office de description détaillée ;</li><li>Soit un attribut WAI-ARIA <code>aria-describedby</code> associant un passage de texte faisant office de description détaillée ;</li><li>Soit un lien ou un bouton adjacent permettant d’accéder à la description détaillée.</li></ul></li><li>Si c’est le cas pour chaque élément <code>&lt;svg&gt;</code>, <strong>le test est validé</strong>.</li></ol><h3 id="test-6" tabindex="-1">Test 6 <a class="header-anchor" href="#test-6" aria-label="Permalink to &quot;Test 6&quot;">​</a></h3><p><strong>Pour chaque image vectorielle (balise <code>&lt;svg&gt;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, ayant une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a>, la référence éventuelle à la <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a> dans l’attribut WAI-ARIA <code>aria-label</code> et la <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a> associée par l’attribut WAI-ARIA <code>aria-labelledby</code> ou <code>aria-describedby</code> sont-elles correctement restituées par les technologies d’assistance ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;svg&gt;</code> porteurs d’information dont la description détaillée est fournie au moyen d’un attribut <code>aria-label</code>, <code>aria-labelledby</code> ou <code>aria-describedby</code> ;</li><li>Pour chaque élément <code>&lt;svg&gt;</code>, vérifier que le contenu de la description détaillée est correctement restitué par les technologies d’assistance ;</li><li>Si c’est le cas pour chaque élément <code>&lt;svg&gt;</code>, <strong>le test est validé</strong>.</li></ol><h3 id="test-7" tabindex="-1">Test 7 <a class="header-anchor" href="#test-7" aria-label="Permalink to &quot;Test 7&quot;">​</a></h3><p><strong>Chaque image bitmap (balise <code>&lt;canvas&gt;</code>), <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, qui nécessite une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a>, vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;canvas&gt;</code> porteurs d’information qui nécessitent une description détaillée ;</li><li>Pour chaque élément <code>&lt;canvas&gt;</code>, vérifier qu’il existe : <ul><li>Soit un attribut WAI-ARIA aria-label contenant l’alternative textuelle et une référence à une description détaillée adjacente ;</li><li>Soit un attribut WAI-ARIA aria-labelledby associant un passage de texte faisant office d’alternative textuelle et un autre faisant office de description détaillée ;</li><li>Soit un contenu textuel entre <code>&lt;canvas&gt;</code> et <code>&lt;/canvas&gt;</code> faisant référence à une description détaillée adjacente à l’image bitmap ;</li><li>Soit un contenu textuel entre <code>&lt;canvas&gt;</code> et <code>&lt;/canvas&gt;</code> faisant office de description détaillée ;</li><li>Soit un lien ou un bouton adjacent permettant d’accéder à la description détaillée.</li></ul></li><li>Si c’est le cas pour chaque élément <code>&lt;canvas&gt;</code>, <strong>le test est validé</strong>.</li></ol><h3 id="test-8" tabindex="-1">Test 8 <a class="header-anchor" href="#test-8" aria-label="Permalink to &quot;Test 8&quot;">​</a></h3><p><strong>Pour chaque image bitmap (balise <code>&lt;canvas&gt;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, qui implémente une référence à une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a> adjacente, cette référence est-elle correctement restituée par les technologies d’assistance ?</strong></p><ol><li>Retrouver dans le document les éléments <code>&lt;canvas&gt;</code> porteurs d’information dont la description détaillée est fournie au moyen d’un attribut <code>aria-label</code>, <code>aria-labelledby</code> ou <code>aria-describedby</code> ;</li><li>Pour chaque élément <code>&lt;canvas&gt;</code>, vérifier que le contenu de la description détaillée est correctement restitué par les technologies d’assistance ;</li><li>Si c’est le cas pour chaque élément <code>&lt;canvas&gt;</code>, <strong>le test est validé</strong>.</li></ol><h3 id="test-9" tabindex="-1">Test 9 <a class="header-anchor" href="#test-9" aria-label="Permalink to &quot;Test 9&quot;">​</a></h3><p><strong>Pour chaque image (balise <code>&lt;img&gt;</code>, <code>&lt;input&gt;</code> avec l’attribut <code>type=&quot;image&quot;</code>, <code>&lt;area&gt;</code>, <code>&lt;object&gt;</code>, <code>&lt;embed&gt;</code>, <code>&lt;svg&gt;</code>, <code>&lt;canvas&gt;</code>, ou possédant un attribut WAI-ARIA <code>role=&quot;img&quot;</code>) <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, qui est accompagnée d’une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a> et qui utilise un attribut WAI-ARIA <code>aria-describedby</code>, l’attribut WAI-ARIA <code>aria-describedby</code> associe-t-il la <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a> ?</strong></p><ol><li>Retrouver dans le document les images (éléments <code>&lt;img&gt;</code>, <code>&lt;input&gt;</code> avec l’attribut <code>type=&quot;image&quot;</code>, <code>&lt;area&gt;</code>, <code>&lt;object&gt;</code>, <code>&lt;embed&gt;</code>, <code>&lt;svg&gt;</code>, <code>&lt;canvas&gt;</code> ou possédant un attribut WAI-ARIA <code>role=&quot;img&quot;</code>) porteuses d’information dont la description détaillée utilise un attribut WAI-ARIA <code>aria-describedby</code> ;</li><li>Pour chaque image, vérifier que le contenu de la description détaillée est correctement restitué par les technologies d’assistance ;</li><li>Si c’est le cas pour chaque image, <strong>le test est validé</strong>.</li></ol><h3 id="test-10" tabindex="-1">Test 10 <a class="header-anchor" href="#test-10" aria-label="Permalink to &quot;Test 10&quot;">​</a></h3><p><strong>Chaque balise possédant un attribut WAI-ARIA <code>role=&quot;img&quot;</code> <a href="/eslint-plugin-rgaa/rgaa/glossaire/image-porteuse-d-information.html">porteuse d’information</a>, qui nécessite une <a href="/eslint-plugin-rgaa/rgaa/glossaire/description-detaillee-image.html">description détaillée</a>, vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments pourvus d’un attribut WAI-ARIA <code>role=&quot;img&quot;</code> porteurs d’information qui nécessitent une description détaillée ;</li><li>Pour chaque élément <code>role=&quot;img&quot;</code>, vérifier qu’il existe : <ul><li>Soit un attribut WAI-ARIA <code>aria-label</code> contenant l’alternative textuelle et une référence à une description détaillée adjacente ;</li><li>Soit un attribut WAI-ARIA <code>aria-labelledby</code> associant un passage de texte faisant office d’alternative textuelle et un autre faisant office de description détaillée ;</li><li>Soit un attribut WAI-ARIA <code>aria-describedby</code> associant un passage de texte faisant office de description détaillée ;</li><li>Soit un lien ou un bouton adjacent permettant d’accéder à la description détaillée.</li></ul></li><li>Si c’est le cas pour chaque élément <code>role=&quot;img&quot;</code>, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-1-1" tabindex="-1">Critère WCAG 1.1.1 <a class="header-anchor" href="#critere-wcag-1-1-1" aria-label="Permalink to &quot;Critère WCAG 1.1.1&quot;">​</a></h3><p><strong>Non-text Content</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G92</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G92" target="_blank" rel="noreferrer">Technique G92</a></li><li><strong>G74</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G74" target="_blank" rel="noreferrer">Technique G74</a></li><li><strong>G73</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G73" target="_blank" rel="noreferrer">Technique G73</a></li><li><strong>H45</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H45" target="_blank" rel="noreferrer">Technique H45</a></li><li><strong>ARIA6</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/ARIA6" target="_blank" rel="noreferrer">Technique ARIA6</a></li></ul>',38)])])}const m=t(o,[["render",r]]);export{p as __pageData,m as default};
