import{_ as t,c as n,o as r,ag as a}from"./chunks/framework.BSOECjnK.js";const f=JSON.parse('{"title":"Pour chaque page web, les contenus peuvent-ils être présentés sans perte d’information ou de fonctionnalité et sans avoir recours soit à un défilement vertical pour une fenêtre ayant une hauteur de 256 px, soit à un défilement horizontal pour une fenêtre ayant une largeur de 320 px (hors cas particuliers) ?","description":"","frontmatter":{"title":"Pour chaque page web, les contenus peuvent-ils être présentés sans perte d’information ou de fonctionnalité et sans avoir recours soit à un défilement vertical pour une fenêtre ayant une hauteur de 256 px, soit à un défilement horizontal pour une fenêtre ayant une largeur de 320 px (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/10-presentation/10.11/index.md","filePath":"rgaa/criteres/10-presentation/10.11/index.md"}'),s={name:"rgaa/criteres/10-presentation/10.11/index.md"};function i(o,e,u,l,c,d){return r(),n("div",null,[...e[0]||(e[0]=[a('<h1 id="pour-chaque-page-web-les-contenus-peuvent-ils-etre-presentes-sans-perte-d-information-ou-de-fonctionnalite-et-sans-avoir-recours-soit-a-un-defilement-vertical-pour-une-fenetre-ayant-une-hauteur-de-256-px-soit-a-un-defilement-horizontal-pour-une-fenetre-ayant-une-largeur-de-320-px-hors-cas-particuliers" tabindex="-1">Pour chaque page web, les contenus peuvent-ils être présentés sans perte d’information ou de fonctionnalité et sans avoir recours soit à un défilement vertical pour une fenêtre ayant une hauteur de 256 px, soit à un défilement horizontal pour une fenêtre ayant une largeur de 320 px (hors cas particuliers) ? <a class="header-anchor" href="#pour-chaque-page-web-les-contenus-peuvent-ils-etre-presentes-sans-perte-d-information-ou-de-fonctionnalite-et-sans-avoir-recours-soit-a-un-defilement-vertical-pour-une-fenetre-ayant-une-hauteur-de-256-px-soit-a-un-defilement-horizontal-pour-une-fenetre-ayant-une-largeur-de-320-px-hors-cas-particuliers" aria-label="Permalink to &quot;Pour chaque page web, les contenus peuvent-ils être présentés sans perte d’information ou de fonctionnalité et sans avoir recours soit à un défilement vertical pour une fenêtre ayant une hauteur de 256 px, soit à un défilement horizontal pour une fenêtre ayant une largeur de 320 px (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Pour chaque page web, lorsque le contenu dont le sens de lecture est horizontal est affiché dans une fenêtre réduite à une largeur de 320 px, l’ensemble des informations et des fonctionnalités sont-elles disponibles sans aucun défilement horizontal (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document si son contenu est conçu pour défiler verticalement (le sens de lecture du texte est horizontal), les informations et fonctionnalités ;</li><li>Réduire la fenêtre d’affichage à une largeur de 320 px et vérifier que les informations et les fonctionnalités restent disponibles sans aucun défilement horizontal ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Pour chaque page web, lorsque le contenu dont le sens de lecture est vertical est affiché dans une fenêtre réduite à une hauteur de 256 px, l’ensemble des informations et des fonctionnalités sont-elles disponibles sans aucun défilement vertical (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document si son contenu est conçu pour défiler horizontalement (le sens de lecture du texte est vertical), les informations et fonctionnalités ;</li><li>Réduire la fenêtre d’affichage à une hauteur de 256 px et vérifier que les informations et les fonctionnalités restent disponibles sans aucun défilement vertical ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-4-10" tabindex="-1">Critère WCAG 1.4.10 <a class="header-anchor" href="#critere-wcag-1-4-10" aria-label="Permalink to &quot;Critère WCAG 1.4.10&quot;">​</a></h3><p><strong>Reflow</strong> (Niveau AA)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>C34</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/C34" target="_blank" rel="noreferrer">Technique C34</a></li><li><strong>C37</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/C37" target="_blank" rel="noreferrer">Technique C37</a></li></ul>',14)])])}const h=t(s,[["render",i]]);export{f as __pageData,h as default};
