import{_ as a,c as t,o as i,ag as n}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Dans chaque page web, les contenus additionnels apparaissant à la prise de focus ou au survol d’un composant d’interface sont-ils contrôlables par l’utilisateur (hors cas particuliers) ?","description":"","frontmatter":{"title":"Dans chaque page web, les contenus additionnels apparaissant à la prise de focus ou au survol d’un [composant d’interface](/rgaa/glossaire/composant-d-interface) sont-ils contrôlables par l’utilisateur (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/10-presentation/10.13/index.md","filePath":"rgaa/criteres/10-presentation/10.13/index.md"}'),s={name:"rgaa/criteres/10-presentation/10.13/index.md"};function l(o,e,r,u,c,d){return i(),t("div",null,[...e[0]||(e[0]=[n('<h1 id="dans-chaque-page-web-les-contenus-additionnels-apparaissant-a-la-prise-de-focus-ou-au-survol-d-un-composant-d-interface-sont-ils-controlables-par-l-utilisateur-hors-cas-particuliers" tabindex="-1">Dans chaque page web, les contenus additionnels apparaissant à la prise de focus ou au survol d’un <a href="/eslint-plugin-rgaa/rgaa/glossaire/composant-d-interface.html">composant d’interface</a> sont-ils contrôlables par l’utilisateur (hors cas particuliers) ? <a class="header-anchor" href="#dans-chaque-page-web-les-contenus-additionnels-apparaissant-a-la-prise-de-focus-ou-au-survol-d-un-composant-d-interface-sont-ils-controlables-par-l-utilisateur-hors-cas-particuliers" aria-label="Permalink to &quot;Dans chaque page web, les contenus additionnels apparaissant à la prise de focus ou au survol d’un [composant d’interface](/rgaa/glossaire/composant-d-interface) sont-ils contrôlables par l’utilisateur (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque contenu additionnel devenant visible à la prise de focus ou au survol d’un <a href="/eslint-plugin-rgaa/rgaa/glossaire/composant-d-interface.html">composant d’interface</a> peut-il être masqué par une action de l’utilisateur sans déplacer le focus ou le pointeur de la souris (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les contenus additionnels devenant visible à la prise de focus ou au survol d’un composant d’interface, à l’exception : <ul><li>Des contenus additionnels contrôlés par l’agent utilisateur (par exemple, les infobulles associées à l’attribut <code>title</code> ou à la validation native d’un formulaire ;</li><li>Des contenus additionnels devenant visibles par une activation de l’utilisateur (par exemple, une fenêtre de dialogue).</li></ul></li><li>Pour chaque contenu additionnel, vérifier que : <ul><li>Soit le contenu additionnel est positionné de façon à ce qu’il ne gêne pas la consultation des autres contenus informatifs sur lesquels il viendrait se superposer (y compris le composant d’interface qui a déclenché son apparition), quelles que soient les conditions de consultation (y compris lors de l’utilisation d’un mécanisme de zoom) ;</li><li>Soit un mécanisme (au clavier) permet de faire disparaître le contenu additionnel (par exemple, la touche Echap).</li></ul></li><li>Si c’est le cas pour chaque contenu additionnel, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque contenu additionnel qui apparait au survol d’un <a href="/eslint-plugin-rgaa/rgaa/glossaire/composant-d-interface.html">composant d’interface</a> peut-il être survolé par le pointeur de la souris sans disparaître (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les contenus additionnels devenant visible au survol d’un composant d’interface, à l’exception : <ul><li>Des contenus additionnels contrôlés par l’agent utilisateur (par exemple, les infobulles associées à l’attribut title ou à la validation native d’un formulaire) ;</li><li>Des contenus additionnels devenant visibles par une activation de l’utilisateur (par exemple, une fenêtre de dialogue).</li></ul></li><li>Pour chaque contenu additionnel, vérifier qu’il peut être survolé par le pointeur de la souris sans disparaître ;</li><li>Si c’est le cas pour chaque contenu additionnel, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Chaque contenu additionnel qui apparaît à la prise de focus ou au survol d’un <a href="/eslint-plugin-rgaa/rgaa/glossaire/composant-d-interface.html">composant d’interface</a> vérifie-t-il une de ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les contenus additionnels devenant visible à la prise de focus ou au survol d’un composant d’interface, à l’exception : <ul><li>Des contenus additionnels contrôlés par l’agent utilisateur (par exemple, les infobulles associées à l’attribut <code>title</code> ou à la validation native d’un formulaire) ;</li><li>Des contenus additionnels devenant visibles par une activation de l’utilisateur (par exemple, une fenêtre de dialogue).</li></ul></li><li>Pour chaque contenu additionnel, vérifier qu’il reste visible : <ul><li>Jusqu’à ce que l’utilisateur retire le pointeur souris ou le focus du contenu additionnel ou du composant d’interface ayant déclenché son apparition ;</li><li>Jusqu’à ce l’utilisateur déclenche le mécanisme prévu pour faire disparaître le contenu additionnel ;</li><li>Jusqu’à ce que l’information proposée par le contenu additionnel ne soit plus valide (par exemple un contenu additionnel signalant l’état “occupé” du composant d’interface que l’utilisateur souhaite activer ou encore un message d’erreur signalé sous la forme d’un contenu additionnel tant que l’utilisateur n’a pas rectifié sa saisie).</li></ul></li><li>Si c’est le cas pour chaque contenu additionnel, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-4-13" tabindex="-1">Critère WCAG 1.4.13 <a class="header-anchor" href="#critere-wcag-1-4-13" aria-label="Permalink to &quot;Critère WCAG 1.4.13&quot;">​</a></h3><p><strong>Content on Hover or Focus</strong> (Niveau AA)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>F95</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F95" target="_blank" rel="noreferrer">Technique F95</a></li></ul>',17)])])}const f=a(s,[["render",l]]);export{h as __pageData,f as default};
