import{_ as a,c as t,o as s,ag as i}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Dans chaque page web, les contenus additionnels apparaissant via les styles CSS uniquement peuvent-ils être rendus visibles au clavier et par tout dispositif de pointage ?","description":"","frontmatter":{"title":"Dans chaque page web, les contenus additionnels apparaissant via les styles CSS uniquement peuvent-ils être rendus visibles au clavier et par tout dispositif de pointage ?"},"headers":[],"relativePath":"rgaa/criteres/10-presentation/10.14/index.md","filePath":"rgaa/criteres/10-presentation/10.14/index.md"}'),n={name:"rgaa/criteres/10-presentation/10.14/index.md"};function o(r,e,l,u,d,c){return s(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="dans-chaque-page-web-les-contenus-additionnels-apparaissant-via-les-styles-css-uniquement-peuvent-ils-etre-rendus-visibles-au-clavier-et-par-tout-dispositif-de-pointage" tabindex="-1">Dans chaque page web, les contenus additionnels apparaissant via les styles CSS uniquement peuvent-ils être rendus visibles au clavier et par tout dispositif de pointage ? <a class="header-anchor" href="#dans-chaque-page-web-les-contenus-additionnels-apparaissant-via-les-styles-css-uniquement-peuvent-ils-etre-rendus-visibles-au-clavier-et-par-tout-dispositif-de-pointage" aria-label="Permalink to &quot;Dans chaque page web, les contenus additionnels apparaissant via les styles CSS uniquement peuvent-ils être rendus visibles au clavier et par tout dispositif de pointage ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, les contenus additionnels apparaissant au survol d’un <a href="/eslint-plugin-rgaa/rgaa/glossaire/composant-d-interface.html">composant d’interface</a> via les styles CSS respectent-ils si nécessaire une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les contenus additionnels devenant visible au survol d’un composant d’interface au moyen d’un mécanisme CSS (<code>pseudo-classe :hover</code>) ;</li><li>Pour chaque contenu additionnel, vérifier que les contenus additionnels apparaissent également : <ul><li>À l’activation du composant au moyen du clavier ou de tout autre dispositif de pointage ;</li><li>À la prise de focus du composant ;</li><li>À l’activation ou à la prise de focus d’un autre composant.</li></ul></li><li>Si c’est le cas pour chaque contenu additionnel, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Dans chaque page web, les contenus additionnels apparaissant au focus d’un <a href="/eslint-plugin-rgaa/rgaa/glossaire/composant-d-interface.html">composant d’interface</a> via les styles CSS respectent-ils si nécessaire une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les contenus additionnels devenant visible à la prise de focus d’un composant d’interface au moyen d’un mécanisme CSS (<code>pseudo-classe :focus</code>) ;</li><li>Pour chaque contenu additionnel, vérifier que les contenus additionnels apparaissent également :</li></ol><ul><li>À l’activation du composant au moyen du clavier ou de tout autre dispositif de pointage ;</li><li>Au survol du composant ;</li><li>À l’activation ou du survol d’un autre composant.</li></ul><ol start="3"><li>Si c’est le cas pour chaque contenu additionnel, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-2-1-1" tabindex="-1">Critère WCAG 2.1.1 <a class="header-anchor" href="#critere-wcag-2-1-1" aria-label="Permalink to &quot;Critère WCAG 2.1.1&quot;">​</a></h3><p><strong>Keyboard</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G202</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G202" target="_blank" rel="noreferrer">Technique G202</a></li></ul>',16)])])}const v=a(n,[["render",o]]);export{h as __pageData,v as default};
