import{_ as t,c as r,o as s,ag as a}from"./chunks/framework.BSOECjnK.js";const u=JSON.parse('{"title":"Dans le site web, des feuilles de styles sont-elles utilisées pour contrôler la présentation de l’information ?","description":"","frontmatter":{"title":"Dans le site web, des [feuilles de styles](/rgaa/glossaire/feuille-de-style) sont-elles utilisées pour contrôler la [présentation de l’information](/rgaa/glossaire/presentation-de-l-information) ?"},"headers":[],"relativePath":"rgaa/criteres/10-presentation/10.1/index.md","filePath":"rgaa/criteres/10-presentation/10.1/index.md"}'),o={name:"rgaa/criteres/10-presentation/10.1/index.md"};function l(i,e,n,c,d,g){return s(),r("div",null,[...e[0]||(e[0]=[a('<h1 id="dans-le-site-web-des-feuilles-de-styles-sont-elles-utilisees-pour-controler-la-presentation-de-l-information" tabindex="-1">Dans le site web, des <a href="/eslint-plugin-rgaa/rgaa/glossaire/feuille-de-style.html">feuilles de styles</a> sont-elles utilisées pour contrôler la <a href="/eslint-plugin-rgaa/rgaa/glossaire/presentation-de-l-information.html">présentation de l’information</a> ? <a class="header-anchor" href="#dans-le-site-web-des-feuilles-de-styles-sont-elles-utilisees-pour-controler-la-presentation-de-l-information" aria-label="Permalink to &quot;Dans le site web, des [feuilles de styles](/rgaa/glossaire/feuille-de-style) sont-elles utilisées pour contrôler la [présentation de l’information](/rgaa/glossaire/presentation-de-l-information) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, les balises servant à la <a href="/eslint-plugin-rgaa/rgaa/glossaire/presentation-de-l-information.html">présentation de l’information</a> ne doivent pas être présentes dans le code source généré des pages. Cette règle est-elle respectée ?</strong></p><ol><li>Vérifier l’absence des éléments de présentation <code>&lt;basefont&gt;</code>, <code>&lt;big&gt;</code>, <code>&lt;blink&gt;</code>, <code>&lt;center&gt;</code>, <code>&lt;font&gt;</code>, <code>&lt;marquee&gt;</code>, <code>&lt;s&gt;</code>, <code>&lt;strike&gt;</code>, <code>&lt;tt&gt;</code> ;</li><li>Vérifier l’absence de l’élément <code>&lt;u&gt;</code> uniquement si le DOCTYPE du document ne correspond pas à HTML 5 ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Dans chaque page web, les attributs servant à la <a href="/eslint-plugin-rgaa/rgaa/glossaire/presentation-de-l-information.html">présentation de l’information</a> ne doivent pas être présents dans le code source généré des pages. Cette règle est-elle respectée ?</strong></p><ol><li>Vérifier l’absence des attributs de présentation : <code>align</code>, <code>alink</code>, <code>background</code>, <code>bgcolor</code>, <code>border</code>, <code>cellpadding</code>, <code>cellspacing</code>, <code>char</code>, <code>charoff</code>, <code>clear</code>, <code>color</code>, <code>compact</code>, <code>frameborder</code>, <code>hspace</code>, <code>link</code>, <code>marginheight</code>, <code>marginwidth</code>, <code>text</code>, <code>valign</code>, <code>vlink</code>, <code>vspace</code>, <code>size</code>(exception faite de l&#39;élément <code>&lt;select&gt;</code>), <code>width</code> (exception faite des éléments <code>&lt;img&gt;</code>, <code>&lt;object&gt;</code>, <code>&lt;embed&gt;</code>, <code>&lt;canvas&gt;</code> et <code>&lt;svg&gt;</code>), <code>height</code> (exception faite des éléments <code>&lt;img&gt;</code>, <code>&lt;object&gt;</code>, <code>&lt;embed&gt;</code>, <code>&lt;canvas&gt;</code> et <code>&lt;svg&gt;</code>) ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Dans chaque page web, l’utilisation des espaces vérifie-t-elle ces conditions ?</strong></p><ol><li>Désactiver les styles (CSS) du document ;</li><li>Vérifier l’absence d’espaces utilisées : <ul><li>Entre les lettres d’un mot ;</li><li>Pour créer des effets de marges ou d’alignement ;</li><li>Pour simuler des tableaux ou des colonnes.</li></ul></li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-3-1" tabindex="-1">Critère WCAG 1.3.1 <a class="header-anchor" href="#critere-wcag-1-3-1" aria-label="Permalink to &quot;Critère WCAG 1.3.1&quot;">​</a></h3><p><strong>Info and Relationships</strong> (Niveau A)</p><h3 id="critere-wcag-1-3-2" tabindex="-1">Critère WCAG 1.3.2 <a class="header-anchor" href="#critere-wcag-1-3-2" aria-label="Permalink to &quot;Critère WCAG 1.3.2&quot;">​</a></h3><p><strong>Meaningful Sequence</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G140</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G140" target="_blank" rel="noreferrer">Technique G140</a></li><li><strong>F32</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F32" target="_blank" rel="noreferrer">Technique F32</a></li><li><strong>F33</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F33" target="_blank" rel="noreferrer">Technique F33</a></li><li><strong>F34</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F34" target="_blank" rel="noreferrer">Technique F34</a></li><li><strong>F48</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F48" target="_blank" rel="noreferrer">Technique F48</a></li><li><strong>C6</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/C6" target="_blank" rel="noreferrer">Technique C6</a></li><li><strong>C8</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/C8" target="_blank" rel="noreferrer">Technique C8</a></li><li><strong>C18</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/C18" target="_blank" rel="noreferrer">Technique C18</a></li><li><strong>C22</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/C22" target="_blank" rel="noreferrer">Technique C22</a></li></ul>',19)])])}const p=t(o,[["render",l]]);export{u as __pageData,p as default};
