import{_ as t,c as a,o as r,ag as l}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Dans chaque page web, les déclarations CSS de couleurs de fond d’élément et de police sont-elles correctement utilisées ?","description":"","frontmatter":{"title":"Dans chaque page web, les déclarations CSS de couleurs de fond d’élément et de police sont-elles correctement utilisées ?"},"headers":[],"relativePath":"rgaa/criteres/10-presentation/10.5/index.md","filePath":"rgaa/criteres/10-presentation/10.5/index.md"}'),o={name:"rgaa/criteres/10-presentation/10.5/index.md"};function n(s,e,c,i,d,u){return r(),a("div",null,[...e[0]||(e[0]=[l('<h1 id="dans-chaque-page-web-les-declarations-css-de-couleurs-de-fond-d-element-et-de-police-sont-elles-correctement-utilisees" tabindex="-1">Dans chaque page web, les déclarations CSS de couleurs de fond d’élément et de police sont-elles correctement utilisées ? <a class="header-anchor" href="#dans-chaque-page-web-les-declarations-css-de-couleurs-de-fond-d-element-et-de-police-sont-elles-correctement-utilisees" aria-label="Permalink to &quot;Dans chaque page web, les déclarations CSS de couleurs de fond d’élément et de police sont-elles correctement utilisées ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, chaque déclaration CSS de couleurs de police (<code>color</code>), d’un élément susceptible de contenir du texte, est-elle accompagnée d’une déclaration de couleur de fond (<code>background</code>, <code>background-color</code>), au moins, héritée d’un parent ?</strong></p><ol><li>Retrouver dans le document les textes mis en couleur, à l’exception des couleurs par défaut (par exemple les liens, etc.) ;</li><li>Déterminer l’élément qui contient le texte et vérifier la présence d’une valeur calculée pour la propriété <code>background-color</code> de l’élément ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Dans chaque page web, chaque déclaration de couleur de fond (<code>background</code>, <code>background-color</code>), d’un élément susceptible de contenir du texte, est-elle accompagnée d’une déclaration de couleur de police (<code>color</code>) au moins, héritée d’un parent ?</strong></p><ol><li>Retrouver dans le document les textes mis en couleur, à l’exception des couleurs par défaut (par exemple les liens, etc.) ;</li><li>Déterminer l’élément qui contient le texte et vérifier la présence d’une valeur calculée pour la propriété <code>color</code> de l’élément ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Dans chaque page web, chaque utilisation d’une image pour créer une couleur de fond d’un élément susceptible de contenir du texte, via CSS (<code>background</code>, <code>background-image</code>), est-elle accompagnée d’une déclaration de couleur de fond (<code>background</code>, <code>background-color</code>), au moins, héritée d’un parent ?</strong></p><ol><li>Retrouver dans le document les textes dont l’arrière-plan est constitué d’une image (propriété background-image) ;</li><li>Déterminer l’élément qui contient le texte et vérifier que si l’image d’arrière-plan est absente, le texte reste lisible ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-4-3" tabindex="-1">Critère WCAG 1.4.3 <a class="header-anchor" href="#critere-wcag-1-4-3" aria-label="Permalink to &quot;Critère WCAG 1.4.3&quot;">​</a></h3><p><strong>Contrast (Minimum)</strong> (Niveau AA)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>F24</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F24" target="_blank" rel="noreferrer">Technique F24</a></li></ul>',17)])])}const m=t(o,[["render",n]]);export{h as __pageData,m as default};
