import{_ as t,c as a,o as n,ag as r}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Dans chaque page web, chaque lien dont la nature n’est pas évidente est-il visible par rapport au texte environnant ?","description":"","frontmatter":{"title":"Dans chaque page web, chaque [lien dont la nature n’est pas évidente](/rgaa/glossaire/lien-dont-la-nature-n-est-pas-evidente) est-il visible par rapport au texte environnant ?"},"headers":[],"relativePath":"rgaa/criteres/10-presentation/10.6/index.md","filePath":"rgaa/criteres/10-presentation/10.6/index.md"}'),i={name:"rgaa/criteres/10-presentation/10.6/index.md"};function s(l,e,o,u,c,d){return n(),a("div",null,[...e[0]||(e[0]=[r('<h1 id="dans-chaque-page-web-chaque-lien-dont-la-nature-n-est-pas-evidente-est-il-visible-par-rapport-au-texte-environnant" tabindex="-1">Dans chaque page web, chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/lien-dont-la-nature-n-est-pas-evidente.html">lien dont la nature n’est pas évidente</a> est-il visible par rapport au texte environnant ? <a class="header-anchor" href="#dans-chaque-page-web-chaque-lien-dont-la-nature-n-est-pas-evidente-est-il-visible-par-rapport-au-texte-environnant" aria-label="Permalink to &quot;Dans chaque page web, chaque [lien dont la nature n’est pas évidente](/rgaa/glossaire/lien-dont-la-nature-n-est-pas-evidente) est-il visible par rapport au texte environnant ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/lien-texte.html">lien texte</a> signalé uniquement par la couleur, et dont la nature n’est pas évidente, vérifie-t-il ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments de type lien (élément <code>&lt;a&gt;</code> ou élément pourvu d’un attribut WAI-ARIA <code>role=&quot;link&quot;</code>) ;</li><li>Pour chaque élément de type lien, s’il peut être confondu avec un texte normal lorsqu’il est signalé uniquement par la couleur, vérifier que le contraste entre la couleur de police du lien et la couleur de police du texte environnant est de 3:1, au moins ;</li><li>Cette vérification doit être faite pour les différents états du lien s’ils sont présentés au moyen d’une couleur différente : l’état non visité, l’état visité, l’état activé, l’état au survol et l’état à la prise de focus ;</li><li>Si c’est le cas pour chaque élément de type lien, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-4-1" tabindex="-1">Critère WCAG 1.4.1 <a class="header-anchor" href="#critere-wcag-1-4-1" aria-label="Permalink to &quot;Critère WCAG 1.4.1&quot;">​</a></h3><p><strong>Use of Color</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G183</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G183" target="_blank" rel="noreferrer">Technique G183</a></li><li><strong>F73</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F73" target="_blank" rel="noreferrer">Technique F73</a></li></ul>',11)])])}const g=t(i,[["render",s]]);export{h as __pageData,g as default};
