import{_ as a,c as t,o as s,ag as n}from"./chunks/framework.BSOECjnK.js";const g=JSON.parse('{"title":"Pour chaque page web, les contenus cachés ont-ils vocation à être ignorés par les technologies d’assistance ?","description":"","frontmatter":{"title":"Pour chaque page web, les [contenus cachés](/rgaa/glossaire/contenu-cache) ont-ils vocation à être ignorés par les technologies d’assistance ?"},"headers":[],"relativePath":"rgaa/criteres/10-presentation/10.8/index.md","filePath":"rgaa/criteres/10-presentation/10.8/index.md"}'),r={name:"rgaa/criteres/10-presentation/10.8/index.md"};function i(o,e,c,l,u,h){return s(),t("div",null,[...e[0]||(e[0]=[n('<h1 id="pour-chaque-page-web-les-contenus-caches-ont-ils-vocation-a-etre-ignores-par-les-technologies-d-assistance" tabindex="-1">Pour chaque page web, les <a href="/eslint-plugin-rgaa/rgaa/glossaire/contenu-cache.html">contenus cachés</a> ont-ils vocation à être ignorés par les technologies d’assistance ? <a class="header-anchor" href="#pour-chaque-page-web-les-contenus-caches-ont-ils-vocation-a-etre-ignores-par-les-technologies-d-assistance" aria-label="Permalink to &quot;Pour chaque page web, les [contenus cachés](/rgaa/glossaire/contenu-cache) ont-ils vocation à être ignorés par les technologies d’assistance ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, chaque contenu caché vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver les contenus cachés (éléments pourvus de l’attribut hidden ou de l’attribut WAI-ARIA aria-hidden, ou bien d’une classe ou d’un ensemble de styles CSS susceptibles de masquer le contenu).</li><li>Pour chaque contenu caché, vérifier que : <ul><li>Soit le contenu caché a vocation à être ignoré par les technologies d’assistance (un élément statistique de visites par exemple) ;</li><li>Soit le contenu caché n’a pas vocation à être ignoré par les technologies d’assistance, et dans ce cas il est rendu restituable par les technologies d’assistance au moyen : <ul><li>Soit d’une action de l’utilisateur réalisable au clavier ou par tout dispositif de pointage sur un élément précédent le contenu caché ;</li><li>Soit d’une fonction de programmation qui repositionne le focus sur le contenu.</li></ul></li></ul></li><li>Si c’est le cas pour chaque contenu caché, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-3-2" tabindex="-1">Critère WCAG 1.3.2 <a class="header-anchor" href="#critere-wcag-1-3-2" aria-label="Permalink to &quot;Critère WCAG 1.3.2&quot;">​</a></h3><p><strong>Meaningful Sequence</strong> (Niveau A)</p><h3 id="critere-wcag-4-1-2" tabindex="-1">Critère WCAG 4.1.2 <a class="header-anchor" href="#critere-wcag-4-1-2" aria-label="Permalink to &quot;Critère WCAG 4.1.2&quot;">​</a></h3><p><strong>Name, Role, Value</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G57</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G57" target="_blank" rel="noreferrer">Technique G57</a></li></ul>',13)])])}const p=a(r,[["render",i]]);export{g as __pageData,p as default};
