import{_ as a,c as t,o as r,ag as o}from"./chunks/framework.BSOECjnK.js";const m=JSON.parse('{"title":"Dans chaque page web, l’information ne doit pas être donnée uniquement par la forme, taille ou position. Cette règle est-elle respectée ?","description":"","frontmatter":{"title":"Dans chaque page web, l’information ne doit pas être donnée uniquement [par la forme, taille ou position](/rgaa/glossaire/indication-donnee-par-la-forme-la-taille-ou-la-position). Cette règle est-elle respectée ?"},"headers":[],"relativePath":"rgaa/criteres/10-presentation/10.9/index.md","filePath":"rgaa/criteres/10-presentation/10.9/index.md"}'),i={name:"rgaa/criteres/10-presentation/10.9/index.md"};function n(l,e,s,u,p,c){return r(),t("div",null,[...e[0]||(e[0]=[o('<h1 id="dans-chaque-page-web-l-information-ne-doit-pas-etre-donnee-uniquement-par-la-forme-taille-ou-position-cette-regle-est-elle-respectee" tabindex="-1">Dans chaque page web, l’information ne doit pas être donnée uniquement <a href="/eslint-plugin-rgaa/rgaa/glossaire/indication-donnee-par-la-forme-la-taille-ou-la-position.html">par la forme, taille ou position</a>. Cette règle est-elle respectée ? <a class="header-anchor" href="#dans-chaque-page-web-l-information-ne-doit-pas-etre-donnee-uniquement-par-la-forme-taille-ou-position-cette-regle-est-elle-respectee" aria-label="Permalink to &quot;Dans chaque page web, l’information ne doit pas être donnée uniquement [par la forme, taille ou position](/rgaa/glossaire/indication-donnee-par-la-forme-la-taille-ou-la-position). Cette règle est-elle respectée ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, pour chaque texte ou ensemble de textes, l’information ne doit pas être donnée uniquement <a href="/eslint-plugin-rgaa/rgaa/glossaire/indication-donnee-par-la-forme-la-taille-ou-la-position.html">par la forme, taille ou position</a>. Cette règle est-elle respectée ?</strong></p><ol><li>Retrouver dans le document les informations d’un texte données par la forme, la taille ou la position ;</li><li>Pour chaque information donnée par la forme, la taille ou la position, vérifier qu’il existe un autre moyen de récupérer cette information ;</li><li>Si c’est le cas pour chaque information, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Dans chaque page web, pour chaque image ou ensemble d’images, l’information ne doit pas être donnée uniquement <a href="/eslint-plugin-rgaa/rgaa/glossaire/indication-donnee-par-la-forme-la-taille-ou-la-position.html">par la forme, taille ou position</a>. Cette règle est-elle respectée ?</strong></p><ol><li>Retrouver dans le document les informations d’une image données par la forme, la taille ou la position ;</li><li>Pour chaque information donnée par la forme, la taille ou la position, vérifier qu’il existe un autre moyen de récupérer cette information ;</li><li>Si c’est le cas pour chaque information, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Dans chaque page web, pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a>, l’information ne doit pas être donnée uniquement <a href="/eslint-plugin-rgaa/rgaa/glossaire/indication-donnee-par-la-forme-la-taille-ou-la-position.html">par la forme, taille ou position</a>. Cette règle est-elle respectée ?</strong></p><ol><li>Retrouver dans le document les informations d’un média temporel données par la forme, la taille ou la position ;</li><li>Pour chaque information donnée par la forme, la taille ou la position, vérifier qu’il existe un autre moyen de récupérer cette information ;</li><li>Si c’est le cas pour chaque information, <strong>le test est validé</strong>.</li></ol><h3 id="test-4" tabindex="-1">Test 4 <a class="header-anchor" href="#test-4" aria-label="Permalink to &quot;Test 4&quot;">​</a></h3><p><strong>Dans chaque page web, pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-non-temporel.html">média non temporel</a>, l’information ne doit pas être donnée uniquement <a href="/eslint-plugin-rgaa/rgaa/glossaire/indication-donnee-par-la-forme-la-taille-ou-la-position.html">par la forme, taille ou position</a>. Cette règle est-elle respectée ?</strong></p><ol><li>Retrouver dans le document les informations d’un média non temporel données par la forme, la taille ou la position ;</li><li>Pour chaque information donnée par la forme, la taille ou la position, vérifier qu’il existe un autre moyen de récupérer cette information ;</li><li>Si c’est le cas pour chaque information, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-3-3" tabindex="-1">Critère WCAG 1.3.3 <a class="header-anchor" href="#critere-wcag-1-3-3" aria-label="Permalink to &quot;Critère WCAG 1.3.3&quot;">​</a></h3><p><strong>Sensory Characteristics</strong> (Niveau A)</p><h3 id="critere-wcag-1-4-1" tabindex="-1">Critère WCAG 1.4.1 <a class="header-anchor" href="#critere-wcag-1-4-1" aria-label="Permalink to &quot;Critère WCAG 1.4.1&quot;">​</a></h3><p><strong>Use of Color</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G96</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G96" target="_blank" rel="noreferrer">Technique G96</a></li><li><strong>G140</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G140" target="_blank" rel="noreferrer">Technique G140</a></li><li><strong>F14</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F14" target="_blank" rel="noreferrer">Technique F14</a></li><li><strong>F26</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F26" target="_blank" rel="noreferrer">Technique F26</a></li></ul>',22)])])}const h=a(i,[["render",n]]);export{m as __pageData,h as default};
