import{_ as i,c as s,o as r,ag as a}from"./chunks/framework.BSOECjnK.js";const p=JSON.parse('{"title":"Pour chaque formulaire qui modifie ou supprime des données, ou qui transmet des réponses à un test ou à un examen, ou dont la validation a des conséquences financières ou juridiques, les données saisies peuvent-elles être modifiées, mises à jour ou récupérées par l’utilisateur ?","description":"","frontmatter":{"title":"Pour chaque [formulaire](/rgaa/glossaire/formulaire) qui modifie ou supprime des données, ou qui transmet des réponses à un test ou à un examen, ou dont la validation a des conséquences financières ou juridiques, les données saisies peuvent-elles être modifiées, mises à jour ou récupérées par l’utilisateur ?"},"headers":[],"relativePath":"rgaa/criteres/11-formulaires/11.12/index.md","filePath":"rgaa/criteres/11-formulaires/11.12/index.md"}'),n={name:"rgaa/criteres/11-formulaires/11.12/index.md"};function o(u,e,t,l,d,c){return r(),s("div",null,[...e[0]||(e[0]=[a('<h1 id="pour-chaque-formulaire-qui-modifie-ou-supprime-des-donnees-ou-qui-transmet-des-reponses-a-un-test-ou-a-un-examen-ou-dont-la-validation-a-des-consequences-financieres-ou-juridiques-les-donnees-saisies-peuvent-elles-etre-modifiees-mises-a-jour-ou-recuperees-par-l-utilisateur" tabindex="-1">Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/formulaire.html">formulaire</a> qui modifie ou supprime des données, ou qui transmet des réponses à un test ou à un examen, ou dont la validation a des conséquences financières ou juridiques, les données saisies peuvent-elles être modifiées, mises à jour ou récupérées par l’utilisateur ? <a class="header-anchor" href="#pour-chaque-formulaire-qui-modifie-ou-supprime-des-donnees-ou-qui-transmet-des-reponses-a-un-test-ou-a-un-examen-ou-dont-la-validation-a-des-consequences-financieres-ou-juridiques-les-donnees-saisies-peuvent-elles-etre-modifiees-mises-a-jour-ou-recuperees-par-l-utilisateur" aria-label="Permalink to &quot;Pour chaque [formulaire](/rgaa/glossaire/formulaire) qui modifie ou supprime des données, ou qui transmet des réponses à un test ou à un examen, ou dont la validation a des conséquences financières ou juridiques, les données saisies peuvent-elles être modifiées, mises à jour ou récupérées par l’utilisateur ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Pour chaque formulaire qui modifie ou supprime des données, ou qui transmet des réponses à un test ou un examen, ou dont la validation a des conséquences financières ou juridiques, la saisie des données vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les formulaires qui modifient ou suppriment des données, ou qui transmettent des réponses à un test ou un examen, ou dont la validation a des conséquences financières ou juridiques ;</li><li>Pour chaque formulaire, vérifier que l’utilisateur peut : <ul><li>Soit modifier ou annuler les données et les actions effectuées sur ces données après la validation du formulaire ;</li><li>Soit vérifier et corriger les données avant la validation d’un formulaire en plusieurs étapes ;</li><li>Soit disposer d’un mécanisme de confirmation explicite (par exemple, une case à cocher ou une étape supplémentaire).</li></ul></li><li>Si c’est le cas pour chaque formulaire retrouvé, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque formulaire dont la validation modifie ou supprime des données à caractère financier, juridique ou personnel vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les formulaires qui modifient ou suppriment des données à caractère financier, juridique ou personnel ;</li><li>Pour chaque formulaire, vérifier que l’utilisateur dispose : <ul><li>Soit d’un mécanisme qui permet de récupérer les données supprimées ou modifiées ;</li><li>Soit d’un mécanisme de demande de confirmation explicite de la suppression ou de la modification (par exemple, une case à cocher ou une étape supplémentaire).</li></ul></li><li>Si c’est le cas pour chaque formulaire retrouvé, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-3-3-4" tabindex="-1">Critère WCAG 3.3.4 <a class="header-anchor" href="#critere-wcag-3-3-4" aria-label="Permalink to &quot;Critère WCAG 3.3.4&quot;">​</a></h3><p><strong>Error Prevention (Legal, Financial, Data)</strong> (Niveau AA)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G98</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G98" target="_blank" rel="noreferrer">Technique G98</a></li><li><strong>G99</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G99" target="_blank" rel="noreferrer">Technique G99</a></li><li><strong>G155</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G155" target="_blank" rel="noreferrer">Technique G155</a></li><li><strong>G164</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G164" target="_blank" rel="noreferrer">Technique G164</a></li><li><strong>G168</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G168" target="_blank" rel="noreferrer">Technique G168</a></li></ul>',14)])])}const f=i(n,[["render",o]]);export{p as __pageData,f as default};
