import{_ as a,c as r,o as t,ag as i}from"./chunks/framework.BSOECjnK.js";const m=JSON.parse('{"title":"Chaque champ de formulaire a-t-il une étiquette ?","description":"","frontmatter":{"title":"Chaque [champ de formulaire](/rgaa/glossaire/champ-de-saisie-de-formulaire) a-t-il une [étiquette](/rgaa/glossaire/etiquette-de-champ-de-formulaire) ?"},"headers":[],"relativePath":"rgaa/criteres/11-formulaires/11.1/index.md","filePath":"rgaa/criteres/11-formulaires/11.1/index.md"}'),l={name:"rgaa/criteres/11-formulaires/11.1/index.md"};function o(s,e,n,u,c,d){return t(),r("div",null,[...e[0]||(e[0]=[i('<h1 id="chaque-champ-de-formulaire-a-t-il-une-etiquette" tabindex="-1">Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/champ-de-saisie-de-formulaire.html">champ de formulaire</a> a-t-il une <a href="/eslint-plugin-rgaa/rgaa/glossaire/etiquette-de-champ-de-formulaire.html">étiquette</a> ? <a class="header-anchor" href="#chaque-champ-de-formulaire-a-t-il-une-etiquette" aria-label="Permalink to &quot;Chaque [champ de formulaire](/rgaa/glossaire/champ-de-saisie-de-formulaire) a-t-il une [étiquette](/rgaa/glossaire/etiquette-de-champ-de-formulaire) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/champ-de-saisie-de-formulaire.html">champ de formulaire</a> vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les champs de formulaire ;</li><li>Pour chaque champ de formulaire, vérifier que le champ de formulaire : <ul><li>Possède un attribut WAI-ARIA <code>aria-labelledby</code> référençant un passage de texte identifié ;</li><li>Possède un attribut WAI-ARIA <code>aria-label</code> ;</li><li>Est associé à un élément <code>&lt;label&gt;</code> ayant un attribut <code>for</code> ;</li><li>Possède un attribut <code>title</code> ;</li><li>Un bouton adjacent au champ de formulaire lui fournit une étiquette visible et un élément <code>&lt;label&gt;</code> visuellement caché ou un attribut WAI-ARIA <code>aria-label</code>, <code>aria-labelledby</code> ou <code>title</code> lui fournit un nom accessible.</li></ul></li><li>Si c’est le cas pour champ de formulaire, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/champ-de-saisie-de-formulaire.html">champ de formulaire</a> associé à une balise <code>&lt;label&gt;</code> ayant un attribut <code>for</code>, vérifie-t-il ces conditions ?</strong></p><ol><li>Retrouver dans le document les champs de formulaire associé à un élément <code>&lt;label&gt;</code> ;</li><li>Pour chaque champ de formulaire, vérifier que : <ul><li>Le champ de formulaire possède un attribut <code>id</code> ;</li><li>La valeur de l’attribut <code>for</code> de l’élément <code>&lt;label&gt;</code> est égale à la valeur de l’attribut <code>id</code>.</li></ul></li><li>Si c’est le cas pour champ de formulaire, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/champ-de-saisie-de-formulaire.html">champ de formulaire</a> ayant une <a href="/eslint-plugin-rgaa/rgaa/glossaire/etiquette-de-champ-de-formulaire.html">étiquette</a> dont le contenu n’est pas visible ou à proximité (masqué, <code>aria-label</code>) ou qui n’est pas <a href="/eslint-plugin-rgaa/rgaa/glossaire/accoles-etiquette-et-champ-accoles.html">accolé</a> au champ (<code>aria-labelledby</code>), vérifie-t-il une de ses conditions ?</strong></p><ol><li>Retrouver dans le document les champs de formulaire dont l’étiquette n’est pas visible ou à proximité (masquée, utilisation de l’attribut aria-label) ou n’est pas accolée au champ (utilisation de l’attribut <code>aria-labelledby</code>) ;</li><li>Pour chaque champ de formulaire, vérifier que le champ de formulaire : <ul><li>soit possède un attribut <code>title</code> dont le contenu permet de comprendre la nature de la saisie attendue ;</li><li>est accompagné d’un passage de texte accolé au champ qui devient visible à la prise de focus permettant de comprendre la nature de la saisie attendue ;</li><li>est accompagné d’un passage de texte visible accolé au champ permettant de comprendre la nature de la saisie attendue.</li></ul></li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-3-1" tabindex="-1">Critère WCAG 1.3.1 <a class="header-anchor" href="#critere-wcag-1-3-1" aria-label="Permalink to &quot;Critère WCAG 1.3.1&quot;">​</a></h3><p><strong>Info and Relationships</strong> (Niveau A)</p><h3 id="critere-wcag-2-4-6" tabindex="-1">Critère WCAG 2.4.6 <a class="header-anchor" href="#critere-wcag-2-4-6" aria-label="Permalink to &quot;Critère WCAG 2.4.6&quot;">​</a></h3><p><strong>Headings and Labels</strong> (Niveau AA)</p><h3 id="critere-wcag-3-3-2" tabindex="-1">Critère WCAG 3.3.2 <a class="header-anchor" href="#critere-wcag-3-3-2" aria-label="Permalink to &quot;Critère WCAG 3.3.2&quot;">​</a></h3><p><strong>Labels or Instructions</strong> (Niveau A)</p><h3 id="critere-wcag-4-1-2" tabindex="-1">Critère WCAG 4.1.2 <a class="header-anchor" href="#critere-wcag-4-1-2" aria-label="Permalink to &quot;Critère WCAG 4.1.2&quot;">​</a></h3><p><strong>Name, Role, Value</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G82</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G82" target="_blank" rel="noreferrer">Technique G82</a></li><li><strong>G131</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G131" target="_blank" rel="noreferrer">Technique G131</a></li><li><strong>H44</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H44" target="_blank" rel="noreferrer">Technique H44</a></li><li><strong>H65</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H65" target="_blank" rel="noreferrer">Technique H65</a></li><li><strong>F68</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F68" target="_blank" rel="noreferrer">Technique F68</a></li><li><strong>F82</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F82" target="_blank" rel="noreferrer">Technique F82</a></li><li><strong>F86</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F86" target="_blank" rel="noreferrer">Technique F86</a></li><li><strong>ARIA6</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/ARIA6" target="_blank" rel="noreferrer">Technique ARIA6</a></li><li><strong>ARIA9</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/ARIA9" target="_blank" rel="noreferrer">Technique ARIA9</a></li><li><strong>ARIA14</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/ARIA14" target="_blank" rel="noreferrer">Technique ARIA14</a></li><li><strong>ARIA16</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/ARIA16" target="_blank" rel="noreferrer">Technique ARIA16</a></li></ul>',23)])])}const g=a(l,[["render",o]]);export{m as __pageData,g as default};
