import{_ as a,c as t,o as r,ag as o}from"./chunks/framework.BSOECjnK.js";const m=JSON.parse('{"title":"Dans chaque formulaire, chaque étiquette de champ et son champ associé sont-ils accolés (hors cas particuliers) ?","description":"","frontmatter":{"title":"Dans chaque [formulaire](/rgaa/glossaire/formulaire), chaque [étiquette de champ](/rgaa/glossaire/etiquette-de-champ-de-formulaire) et son champ associé sont-ils [accolés](/rgaa/glossaire/accoles-etiquette-et-champ-accoles) (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/11-formulaires/11.4/index.md","filePath":"rgaa/criteres/11-formulaires/11.4/index.md"}'),s={name:"rgaa/criteres/11-formulaires/11.4/index.md"};function i(l,e,c,u,d,n){return r(),t("div",null,[...e[0]||(e[0]=[o('<h1 id="dans-chaque-formulaire-chaque-etiquette-de-champ-et-son-champ-associe-sont-ils-accoles-hors-cas-particuliers" tabindex="-1">Dans chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/formulaire.html">formulaire</a>, chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/etiquette-de-champ-de-formulaire.html">étiquette de champ</a> et son champ associé sont-ils <a href="/eslint-plugin-rgaa/rgaa/glossaire/accoles-etiquette-et-champ-accoles.html">accolés</a> (hors cas particuliers) ? <a class="header-anchor" href="#dans-chaque-formulaire-chaque-etiquette-de-champ-et-son-champ-associe-sont-ils-accoles-hors-cas-particuliers" aria-label="Permalink to &quot;Dans chaque [formulaire](/rgaa/glossaire/formulaire), chaque [étiquette de champ](/rgaa/glossaire/etiquette-de-champ-de-formulaire) et son champ associé sont-ils [accolés](/rgaa/glossaire/accoles-etiquette-et-champ-accoles) (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/etiquette-de-champ-de-formulaire.html">étiquette de champ</a> et son <a href="/eslint-plugin-rgaa/rgaa/glossaire/champ-de-saisie-de-formulaire.html">champ</a> associé sont-ils <a href="/eslint-plugin-rgaa/rgaa/glossaire/accoles-etiquette-et-champ-accoles.html">accolés</a> ?</strong></p><ol><li>Retrouver dans le document les champs de formulaire ;</li><li>Pour chaque champ de formulaire, vérifier qu’il est accolé à son étiquette ;</li><li>Si c’est le cas pour chaque champ de formulaire, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/etiquette-de-champ-de-formulaire.html">étiquette</a> <a href="/eslint-plugin-rgaa/rgaa/glossaire/accoles-etiquette-et-champ-accoles.html">accolée</a> à un <a href="/eslint-plugin-rgaa/rgaa/glossaire/champ-de-saisie-de-formulaire.html">champ</a> (à l’exception des cases à cocher, bouton radio ou balises ayant un attribut WAI-ARIA <code>role=&quot;checkbox&quot;</code>, <code>role=&quot;radio&quot;</code> ou <code>role=&quot;switch&quot;</code>), vérifie-t-elle ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les champs de formulaire qui ne sont pas des éléments <code>&lt;input&gt;</code> de type <code>checkbox</code> ou de type <code>radio</code> ou des éléments ayant un attribut WAI-ARIA <code>role=&quot;checkbox&quot;</code>, <code>role=&quot;radio&quot;</code> ou <code>role=&quot;switch</code>&quot;;</li><li>Pour chaque champ de formulaire, vérifier que l’étiquette est visuellement accolée : <ul><li>Immédiatement au-dessus ou à gauche du champ de formulaire lorsque le sens de lecture de la langue de l’étiquette est de gauche à droite ;</li><li>Immédiatement au-dessus ou à droite du champ de formulaire lorsque le sens de lecture de la langue de l’étiquette est de droite à gauche.</li></ul></li><li>Si c’est le cas pour chaque champ de formulaire, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/etiquette-de-champ-de-formulaire.html">étiquette</a> <a href="/eslint-plugin-rgaa/rgaa/glossaire/accoles-etiquette-et-champ-accoles.html">accolée</a> à un <a href="/eslint-plugin-rgaa/rgaa/glossaire/champ-de-saisie-de-formulaire.html">champ</a> de type <code>checkbox</code> ou <code>radio</code> ou à une balise ayant un attribut WAI-ARIA <code>role=&quot;checkbox&quot;</code>, <code>role=&quot;radio&quot;</code> ou <code>role=&quot;switch&quot;</code>, vérifie-t-elle ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les champs de formulaire qui sont <code>&lt;input&gt;</code> de type <code>checkbox</code> ou de type <code>radio</code> ou des éléments ayant un attribut WAI-ARIA <code>role=&quot;checkbox&quot;</code>, <code>role=&quot;radio&quot;</code> ou <code>role=&quot;switch</code>&quot;;</li><li>Pour chaque champ de formulaire, vérifier que l’étiquette est visuellement accolée : <ul><li>Immédiatement au-dessous ou à droite du champ de formulaire lorsque le sens de lecture de la langue de l’étiquette est de gauche à droite ;</li><li>Immédiatement au-dessous ou à gauche du champ de formulaire lorsque le sens de lecture de la langue de l’étiquette est de droite à gauche.</li></ul></li><li>Si c’est le cas pour chaque champ de formulaire, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-3-3-2" tabindex="-1">Critère WCAG 3.3.2 <a class="header-anchor" href="#critere-wcag-3-3-2" aria-label="Permalink to &quot;Critère WCAG 3.3.2&quot;">​</a></h3><p><strong>Labels or Instructions</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G162</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G162" target="_blank" rel="noreferrer">Technique G162</a></li></ul>',17)])])}const q=a(s,[["render",i]]);export{m as __pageData,q as default};
