import{_ as a,c as r,o as t,ag as i}from"./chunks/framework.BSOECjnK.js";const m=JSON.parse('{"title":"Dans chaque page web, les raccourcis clavier n’utilisant qu’une seule touche (lettre minuscule ou majuscule, ponctuation, chiffre ou symbole) sont-ils contrôlables par l’utilisateur ?","description":"","frontmatter":{"title":"Dans chaque page web, les [raccourcis clavier](/rgaa/glossaire/raccourci-clavier) n’utilisant qu’une seule touche (lettre minuscule ou majuscule, ponctuation, chiffre ou symbole) sont-ils contrôlables par l’utilisateur ?"},"headers":[],"relativePath":"rgaa/criteres/12-navigation/12.10/index.md","filePath":"rgaa/criteres/12-navigation/12.10/index.md"}'),s={name:"rgaa/criteres/12-navigation/12.10/index.md"};function c(l,e,u,o,n,h){return t(),r("div",null,[...e[0]||(e[0]=[i('<h1 id="dans-chaque-page-web-les-raccourcis-clavier-n-utilisant-qu-une-seule-touche-lettre-minuscule-ou-majuscule-ponctuation-chiffre-ou-symbole-sont-ils-controlables-par-l-utilisateur" tabindex="-1">Dans chaque page web, les <a href="/eslint-plugin-rgaa/rgaa/glossaire/raccourci-clavier.html">raccourcis clavier</a> n’utilisant qu’une seule touche (lettre minuscule ou majuscule, ponctuation, chiffre ou symbole) sont-ils contrôlables par l’utilisateur ? <a class="header-anchor" href="#dans-chaque-page-web-les-raccourcis-clavier-n-utilisant-qu-une-seule-touche-lettre-minuscule-ou-majuscule-ponctuation-chiffre-ou-symbole-sont-ils-controlables-par-l-utilisateur" aria-label="Permalink to &quot;Dans chaque page web, les [raccourcis clavier](/rgaa/glossaire/raccourci-clavier) n’utilisant qu’une seule touche (lettre minuscule ou majuscule, ponctuation, chiffre ou symbole) sont-ils contrôlables par l’utilisateur ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/raccourci-clavier.html">raccourci clavier</a> n’utilisant qu’une seule touche (lettre minuscule ou majuscule, ponctuation, chiffre ou symbole) vérifie-t-il l’une de ces conditions ?</strong></p><ol><li>Retrouver dans le document l’ensemble des raccourcis clavier proposés à l’utilisateur ;</li><li>Pour chaque raccourci clavier, vérifier que : <ul><li>Soit un mécanisme est disponible pour désactiver le raccourci clavier ;</li><li>Soit un mécanisme est disponible pour configurer la touche de raccourci clavier au moyen des touches de modification (Ctrl, Alt, Maj, etc.) ;</li><li>Soit, dans le cas d’un composant d’interface utilisateur, le raccourci clavier qui lui est associé ne peut être activé que si le focus clavier est sur ce composant.</li></ul></li><li>Si c’est le cas pour chaque raccourci clavier, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-2-1-4" tabindex="-1">Critère WCAG 2.1.4 <a class="header-anchor" href="#critere-wcag-2-1-4" aria-label="Permalink to &quot;Critère WCAG 2.1.4&quot;">​</a></h3><p><strong>Character Key Shortcuts</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>F99</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F99" target="_blank" rel="noreferrer">Technique F99</a></li><li><strong>G217</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G217" target="_blank" rel="noreferrer">Technique G217</a></li></ul>',11)])])}const d=a(s,[["render",c]]);export{m as __pageData,d as default};
