import{_ as a,c as r,o as t,ag as s}from"./chunks/framework.BSOECjnK.js";const g=JSON.parse('{"title":"Dans chaque ensemble de pages, le moteur de recherche est-il atteignable de manière identique ?","description":"","frontmatter":{"title":"Dans chaque [ensemble de pages](/rgaa/glossaire/ensemble-de-pages), le [moteur de recherche](/rgaa/glossaire/moteur-de-recherche-interne-a-un-site-web) est-il atteignable de manière identique ?"},"headers":[],"relativePath":"rgaa/criteres/12-navigation/12.5/index.md","filePath":"rgaa/criteres/12-navigation/12.5/index.md"}'),n={name:"rgaa/criteres/12-navigation/12.5/index.md"};function l(i,e,o,c,u,h){return t(),r("div",null,[...e[0]||(e[0]=[s('<h1 id="dans-chaque-ensemble-de-pages-le-moteur-de-recherche-est-il-atteignable-de-maniere-identique" tabindex="-1">Dans chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/ensemble-de-pages.html">ensemble de pages</a>, le <a href="/eslint-plugin-rgaa/rgaa/glossaire/moteur-de-recherche-interne-a-un-site-web.html">moteur de recherche</a> est-il atteignable de manière identique ? <a class="header-anchor" href="#dans-chaque-ensemble-de-pages-le-moteur-de-recherche-est-il-atteignable-de-maniere-identique" aria-label="Permalink to &quot;Dans chaque [ensemble de pages](/rgaa/glossaire/ensemble-de-pages), le [moteur de recherche](/rgaa/glossaire/moteur-de-recherche-interne-a-un-site-web) est-il atteignable de manière identique ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/ensemble-de-pages.html">ensemble de pages</a>, le <a href="/eslint-plugin-rgaa/rgaa/glossaire/moteur-de-recherche-interne-a-un-site-web.html">moteur de recherche</a> est-il accessible à partir d’une fonctionnalité identique ?</strong></p><ol><li>Choisir une page de l’échantillon appartenant au même ensemble que la page en cours d’audit ;</li><li>Comparer le code source (généré côté client) des deux pages et vérifier que le moyen d’accès au moteur de recherche est toujours le même (un champ de formulaire, par exemple) ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Dans chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/ensemble-de-pages.html">ensemble de pages</a>, la fonctionnalité vers le <a href="/eslint-plugin-rgaa/rgaa/glossaire/moteur-de-recherche-interne-a-un-site-web.html">moteur de recherche</a> est-elle située à la même place dans la présentation ?</strong></p><ol><li>Choisir une page de l’échantillon appartenant au même ensemble que la page en cours d’audit ;</li><li>Comparer visuellement les deux pages et vérifier que le moyen d’accès au moteur de recherche est toujours à la même place dans la présentation ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Dans chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/ensemble-de-pages.html">ensemble de pages</a>, la fonctionnalité vers le <a href="/eslint-plugin-rgaa/rgaa/glossaire/moteur-de-recherche-interne-a-un-site-web.html">moteur de recherche</a> se présente-t-elle toujours dans le même ordre relatif dans le code source ?</strong></p><ol><li>Choisir une page de l’échantillon appartenant au même ensemble que la page en cours d’audit ;</li><li>Comparer le code source (généré côté client) des deux pages et vérifier que le moyen d’accès au moteur de recherche est toujours à la même place dans la structure (par rapport à l’ordre relatif des éléments de la page, par exemple il est toujours en haut de page) ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-3-2-3" tabindex="-1">Critère WCAG 3.2.3 <a class="header-anchor" href="#critere-wcag-3-2-3" aria-label="Permalink to &quot;Critère WCAG 3.2.3&quot;">​</a></h3><p><strong>Consistent Navigation</strong> (Niveau AA)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G61</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G61" target="_blank" rel="noreferrer">Technique G61</a></li><li><strong>F66</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F66" target="_blank" rel="noreferrer">Technique F66</a></li></ul>',17)])])}const m=a(n,[["render",l]]);export{g as __pageData,m as default};
