import{_ as a,c as r,o as i,ag as t}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Dans chaque page web, un lien d’évitement ou d’accès rapide à la zone de contenu principal est-il présent (hors cas particuliers) ?","description":"","frontmatter":{"title":"Dans chaque page web, un [lien d’évitement ou d’accès rapide](/rgaa/glossaire/liens-d-evitement-ou-d-acces-rapide) à la [zone de contenu principal](/rgaa/glossaire/zone-de-contenu-principal) est-il présent (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/12-navigation/12.7/index.md","filePath":"rgaa/criteres/12-navigation/12.7/index.md"}'),n={name:"rgaa/criteres/12-navigation/12.7/index.md"};function l(s,e,o,c,u,d){return i(),r("div",null,[...e[0]||(e[0]=[t('<h1 id="dans-chaque-page-web-un-lien-d-evitement-ou-d-acces-rapide-a-la-zone-de-contenu-principal-est-il-present-hors-cas-particuliers" tabindex="-1">Dans chaque page web, un <a href="/eslint-plugin-rgaa/rgaa/glossaire/liens-d-evitement-ou-d-acces-rapide.html">lien d’évitement ou d’accès rapide</a> à la <a href="/eslint-plugin-rgaa/rgaa/glossaire/zone-de-contenu-principal.html">zone de contenu principal</a> est-il présent (hors cas particuliers) ? <a class="header-anchor" href="#dans-chaque-page-web-un-lien-d-evitement-ou-d-acces-rapide-a-la-zone-de-contenu-principal-est-il-present-hors-cas-particuliers" aria-label="Permalink to &quot;Dans chaque page web, un [lien d’évitement ou d’accès rapide](/rgaa/glossaire/liens-d-evitement-ou-d-acces-rapide) à la [zone de contenu principal](/rgaa/glossaire/zone-de-contenu-principal) est-il présent (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, un lien permet-il d’éviter la <a href="/eslint-plugin-rgaa/rgaa/glossaire/zone-de-contenu-principal.html">zone de contenu principal</a> ou d’y accéder (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document la zone de contenu principal (indiquée par l’élément main visible) ;</li><li>Vérifier que la zone : <ul><li>Soit peut être évitée au moyen d’un lien d’évitement précédant directement la zone dans l’ordre du code source ;</li><li>Soit peut être atteinte au moyen d’un lien d’accès rapide visible à la prise de focus lors d’une tabulation.</li></ul></li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Dans chaque ensemble de pages, le <a href="/eslint-plugin-rgaa/rgaa/glossaire/liens-d-evitement-ou-d-acces-rapide.html">lien d’évitement ou d’accès rapide</a> à la <a href="/eslint-plugin-rgaa/rgaa/glossaire/zone-de-contenu-principal.html">zone de contenu principal</a> vérifie-t-il ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document la zone de contenu principal (indiquée par l’élément main visible) ;</li><li>Vérifier que le lien d’évitement ou d’accès rapide à la zone est : <ul><li>Situé à la même place dans la présentation ;</li><li>Présent toujours dans le même ordre relatif dans le code source (généré côté client) ;</li><li>Visible à la prise de focus lors d’une tabulation ;</li><li>Fonctionnel.</li></ul></li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><p>Note : lorsque le site web est constitué d&#39;une seule page, l&#39;obligation de la présence d&#39;un lien d&#39;accès rapide est liée au contexte de la page (présence ou absence de navigation ou de contenus additionnels, par exemple). Le critère peut être considéré comme non applicable lorsqu&#39;il est avéré qu&#39;un lien d&#39;accès rapide est inutile.</p><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-2-4-1" tabindex="-1">Critère WCAG 2.4.1 <a class="header-anchor" href="#critere-wcag-2-4-1" aria-label="Permalink to &quot;Critère WCAG 2.4.1&quot;">​</a></h3><p><strong>Bypass Blocks</strong> (Niveau A)</p><h3 id="critere-wcag-2-4-3" tabindex="-1">Critère WCAG 2.4.3 <a class="header-anchor" href="#critere-wcag-2-4-3" aria-label="Permalink to &quot;Critère WCAG 2.4.3&quot;">​</a></h3><p><strong>Focus Order</strong> (Niveau A)</p><h3 id="critere-wcag-3-2-3" tabindex="-1">Critère WCAG 3.2.3 <a class="header-anchor" href="#critere-wcag-3-2-3" aria-label="Permalink to &quot;Critère WCAG 3.2.3&quot;">​</a></h3><p><strong>Consistent Navigation</strong> (Niveau AA)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G1</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G1" target="_blank" rel="noreferrer">Technique G1</a></li><li><strong>G59</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G59" target="_blank" rel="noreferrer">Technique G59</a></li><li><strong>G123</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G123" target="_blank" rel="noreferrer">Technique G123</a></li><li><strong>G124</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G124" target="_blank" rel="noreferrer">Technique G124</a></li><li><strong>SCR28</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SCR28" target="_blank" rel="noreferrer">Technique SCR28</a></li><li><strong>F66</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F66" target="_blank" rel="noreferrer">Technique F66</a></li></ul>',19)])])}const g=a(n,[["render",l]]);export{h as __pageData,g as default};
