import{_ as r,c as a,o as t,ag as n}from"./chunks/framework.BSOECjnK.js";const g=JSON.parse('{"title":"Dans chaque page web, l’ordre de tabulation est-il cohérent ?","description":"","frontmatter":{"title":"Dans chaque page web, l’[ordre de tabulation](/rgaa/glossaire/ordre-de-tabulation) est-il [cohérent](/rgaa/glossaire/comprehensible-ordre-de-lecture) ?"},"headers":[],"relativePath":"rgaa/criteres/12-navigation/12.8/index.md","filePath":"rgaa/criteres/12-navigation/12.8/index.md"}'),l={name:"rgaa/criteres/12-navigation/12.8/index.md"};function s(i,e,o,u,c,h){return t(),a("div",null,[...e[0]||(e[0]=[n('<h1 id="dans-chaque-page-web-l-ordre-de-tabulation-est-il-coherent" tabindex="-1">Dans chaque page web, l’<a href="/eslint-plugin-rgaa/rgaa/glossaire/ordre-de-tabulation.html">ordre de tabulation</a> est-il <a href="/eslint-plugin-rgaa/rgaa/glossaire/comprehensible-ordre-de-lecture.html">cohérent</a> ? <a class="header-anchor" href="#dans-chaque-page-web-l-ordre-de-tabulation-est-il-coherent" aria-label="Permalink to &quot;Dans chaque page web, l’[ordre de tabulation](/rgaa/glossaire/ordre-de-tabulation) est-il [cohérent](/rgaa/glossaire/comprehensible-ordre-de-lecture) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, l’<a href="/eslint-plugin-rgaa/rgaa/glossaire/ordre-de-tabulation.html">ordre de tabulation</a> dans le contenu est-il <a href="/eslint-plugin-rgaa/rgaa/glossaire/comprehensible-ordre-de-lecture.html">cohérent</a> ?</strong></p><ol><li>Parcourir dans le document l’ensemble des contenus au moyen de la touche de tabulation vers l’avant (touche Tab) et vers l’arrière (touches Maj+Tab) ;</li><li>Vérifier que l’ordre de déplacement du focus reste cohérent relativement au contenu considéré (par exemple, l’ordre de tabulation dans une fenêtre modale ne doit considérer que les éléments d’interface présents au sein de cette fenêtre) ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><p>Note : il n&#39;est pas obligatoire que la tabulation suive l&#39;ordre de lecture naturel (de gauche à droite et de haut en bas par exemple) tant que les éléments sont accessibles dans un ordre cohérent.</p><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/script.html">script</a> qui met à jour ou insère un contenu, l’<a href="/eslint-plugin-rgaa/rgaa/glossaire/ordre-de-tabulation.html">ordre de tabulation</a> reste-t-il <a href="/eslint-plugin-rgaa/rgaa/glossaire/comprehensible-ordre-de-lecture.html">cohérent</a> ?</strong></p><ol><li>Retrouver dans le document l’ensemble des contenus insérés au moyen d’un script (affichage d’éléments masqués, mise jour de contenu via AJAX par exemple) ;</li><li>Positionner la tabulation sur l’élément déclencheur et l’activer ;</li><li>Après l’affichage du contenu mis à jour, vérifier que la tabulation reste cohérente (repositionnement correct du focus) ;</li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-2-4-3" tabindex="-1">Critère WCAG 2.4.3 <a class="header-anchor" href="#critere-wcag-2-4-3" aria-label="Permalink to &quot;Critère WCAG 2.4.3&quot;">​</a></h3><p><strong>Focus Order</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G59</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G59" target="_blank" rel="noreferrer">Technique G59</a></li><li><strong>H4</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H4" target="_blank" rel="noreferrer">Technique H4</a></li><li><strong>F44</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F44" target="_blank" rel="noreferrer">Technique F44</a></li><li><strong>F85</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F85" target="_blank" rel="noreferrer">Technique F85</a></li><li><strong>SCR26</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SCR26" target="_blank" rel="noreferrer">Technique SCR26</a></li><li><strong>SCR27</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SCR27" target="_blank" rel="noreferrer">Technique SCR27</a></li><li><strong>SCR37</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SCR37" target="_blank" rel="noreferrer">Technique SCR37</a></li><li><strong>C27</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/C27" target="_blank" rel="noreferrer">Technique C27</a></li></ul>',15)])])}const p=r(l,[["render",s]]);export{g as __pageData,p as default};
