import{_ as a,c as t,o as r,ag as i}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Dans chaque page web, la navigation ne doit pas contenir de piège au clavier. Cette règle est-elle respectée ?","description":"","frontmatter":{"title":"Dans chaque page web, la navigation ne doit pas contenir de piège au clavier. Cette règle est-elle respectée ?"},"headers":[],"relativePath":"rgaa/criteres/12-navigation/12.9/index.md","filePath":"rgaa/criteres/12-navigation/12.9/index.md"}'),n={name:"rgaa/criteres/12-navigation/12.9/index.md"};function s(l,e,o,c,u,d){return r(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="dans-chaque-page-web-la-navigation-ne-doit-pas-contenir-de-piege-au-clavier-cette-regle-est-elle-respectee" tabindex="-1">Dans chaque page web, la navigation ne doit pas contenir de piège au clavier. Cette règle est-elle respectée ? <a class="header-anchor" href="#dans-chaque-page-web-la-navigation-ne-doit-pas-contenir-de-piege-au-clavier-cette-regle-est-elle-respectee" aria-label="Permalink to &quot;Dans chaque page web, la navigation ne doit pas contenir de piège au clavier. Cette règle est-elle respectée ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/prise-de-focus.html">élément recevant le focus</a> vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document l’ensemble des éléments d’interface susceptibles de recevoir le focus (au moyen de la tabulation ou au moyen d’un script) ;</li><li>Pour chaque élément d’interface, vérifier que l’utilisateur peut atteindre l’élément suivant ou précédent pouvant recevoir le focus : <ul><li>Soit au moyen de la touche de tabulation (Tab ou Maj+Tab) ;</li><li>Soit au moyen d’une autre interaction clavier dont l’utilisateur est informé (par exemple, les flèches de direction).</li></ul></li><li>Si c’est le cas pour chaque élément d’interface, <strong>le test est validé</strong>.</li></ol><p>Note : certains éléments d&#39;interface complexes, comme un groupe de boutons radio, une liste de sélection et tous les composants développés avec WAI-ARIA font appel à des navigations optimisées qui utilisent généralement les flèches de direction pour passer d&#39;une partie du composant à l&#39;autre. Par exemple, dans un groupe de boutons radio les options sont navigables avec les flèches de direction. De même dans un système d&#39;onglets l&#39;utilisateur active les onglets avec les flèches de direction. Le test sur le piège au clavier se limite alors à vérifier que le composant est atteint avec la tabulation et qu&#39;il est possible de passer au composant suivant ou revenir au composant précédent.</p><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-2-1-1" tabindex="-1">Critère WCAG 2.1.1 <a class="header-anchor" href="#critere-wcag-2-1-1" aria-label="Permalink to &quot;Critère WCAG 2.1.1&quot;">​</a></h3><p><strong>Keyboard</strong> (Niveau A)</p><h3 id="critere-wcag-2-1-2" tabindex="-1">Critère WCAG 2.1.2 <a class="header-anchor" href="#critere-wcag-2-1-2" aria-label="Permalink to &quot;Critère WCAG 2.1.2&quot;">​</a></h3><p><strong>No Keyboard Trap</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G21</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G21" target="_blank" rel="noreferrer">Technique G21</a></li><li><strong>H91</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H91" target="_blank" rel="noreferrer">Technique H91</a></li><li><strong>F10</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F10" target="_blank" rel="noreferrer">Technique F10</a></li></ul>',14)])])}const g=a(n,[["render",s]]);export{h as __pageData,g as default};
