import{_ as t,c as s,o as a,ag as o}from"./chunks/framework.BSOECjnK.js";const f=JSON.parse('{"title":"Dans chaque page web, les changements brusques de luminosité ou les effets de flash sont-ils correctement utilisés ?","description":"","frontmatter":{"title":"Dans chaque page web, [les changements brusques de luminosité ou les effets de flash](/rgaa/glossaire/changement-brusque-de-luminosite-ou-effet-de-flash) sont-ils correctement utilisés ?"},"headers":[],"relativePath":"rgaa/criteres/13-consultation/13.7/index.md","filePath":"rgaa/criteres/13-consultation/13.7/index.md"}'),n={name:"rgaa/criteres/13-consultation/13.7/index.md"};function i(l,e,r,u,c,d){return a(),s("div",null,[...e[0]||(e[0]=[o('<h1 id="dans-chaque-page-web-les-changements-brusques-de-luminosite-ou-les-effets-de-flash-sont-ils-correctement-utilises" tabindex="-1">Dans chaque page web, <a href="/eslint-plugin-rgaa/rgaa/glossaire/changement-brusque-de-luminosite-ou-effet-de-flash.html">les changements brusques de luminosité ou les effets de flash</a> sont-ils correctement utilisés ? <a class="header-anchor" href="#dans-chaque-page-web-les-changements-brusques-de-luminosite-ou-les-effets-de-flash-sont-ils-correctement-utilises" aria-label="Permalink to &quot;Dans chaque page web, [les changements brusques de luminosité ou les effets de flash](/rgaa/glossaire/changement-brusque-de-luminosite-ou-effet-de-flash) sont-ils correctement utilisés ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, chaque image ou élément multimédia (balise <code>&lt;video&gt;</code>, balise <code>&lt;img&gt;</code>, balise <code>&lt;svg&gt;</code>, balise <code>&lt;canvas&gt;</code>, balise <code>&lt;embed&gt;</code> ou balise <code>&lt;object&gt;</code>) qui provoque un changement brusque de luminosité ou un effet de flash vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les contenus clignotants ou qui provoquent des effets de flash de type image animée, vidéo (cf. note) ou animation (éléments <code>&lt;img&gt;</code>, <code>&lt;svg&gt;</code>, <code>&lt;canvas&gt;</code>, <code>&lt;embed&gt;</code>, <code>&lt;object&gt;</code> ou <code>&lt;video&gt;</code>) ;</li><li>Pour chaque contenu clignotant ou provoquant des effets de flash, vérifier que : <ul><li>Soit la fréquence de l’effet est inférieur à 3 par seconde ;</li><li>Soit la surface cumulée est inférieure à 21824 pixels.</li></ul></li><li>Si c’est le cas pour chaque contenu clignotant ou provoquant des effets de flash, <strong>le test est validé</strong>.</li></ol><p>Note : l&#39;évaluation de ce critère peut être complexe. Lorsque l&#39;effet est géré par un script ou au moyen de CSS, l&#39;analyse du code est suffisante. L&#39;outil PEAT permet d&#39;analyser les vidéos au format .avi, par exemple. Un exemple de vidéo ayant provoqué des crises d&#39;épilepsie peut être consulté ici : London 2012 Olympics Seizure (<a href="https://www.youtube.com/watch?v=vs0hfhSje9M" target="_blank" rel="noreferrer">https://www.youtube.com/watch?v=vs0hfhSje9M</a>).</p><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Dans chaque page web, chaque script qui provoque <a href="/eslint-plugin-rgaa/rgaa/glossaire/changement-brusque-de-luminosite-ou-effet-de-flash.html">un changement brusque de luminosité ou un effet de flash</a> vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les contenus clignotants ou qui provoquent des effets de flash obtenus au moyen d’un script ;</li><li>Pour chaque contenu clignotant ou provoquant des effets de flash, vérifier que : <ul><li>Soit la fréquence de l’effet est inférieur à 3 par seconde ;</li><li>Soit la surface cumulée est inférieure à 21824 pixels.</li></ul></li><li>Si c’est le cas pour chaque contenu clignotant ou provoquant des effets de flash, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Dans chaque page web, chaque mise en forme CSS qui provoque <a href="/eslint-plugin-rgaa/rgaa/glossaire/changement-brusque-de-luminosite-ou-effet-de-flash.html">un changement brusque de luminosité ou un effet de flash</a> vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les contenus clignotants ou qui provoquent des effets de flash obtenus au moyen d’une animation CSS ;</li><li>Pour chaque contenu clignotant ou provoquant des effets de flash, vérifier que : <ul><li>Soit la fréquence de l’effet est inférieur à 3 par seconde ;</li><li>Soit la surface cumulée est inférieure à 21824 pixels.</li></ul></li><li>Si c’est le cas pour chaque contenu clignotant ou provoquant des effets de flash, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-2-3-1" tabindex="-1">Critère WCAG 2.3.1 <a class="header-anchor" href="#critere-wcag-2-3-1" aria-label="Permalink to &quot;Critère WCAG 2.3.1&quot;">​</a></h3><p><strong>Three Flashes or Below Threshold</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G15</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G15" target="_blank" rel="noreferrer">Technique G15</a></li><li><strong>G19</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G19" target="_blank" rel="noreferrer">Technique G19</a></li><li><strong>G176</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G176" target="_blank" rel="noreferrer">Technique G176</a></li></ul>',18)])])}const g=t(n,[["render",i]]);export{f as __pageData,g as default};
