import{_ as t,c as a,o as r,ag as l}from"./chunks/framework.BSOECjnK.js";const m=JSON.parse('{"title":"Chaque son déclenché automatiquement est-il contrôlable par l’utilisateur ?","description":"","frontmatter":{"title":"Chaque son déclenché automatiquement est-il [contrôlable](/rgaa/glossaire/controle-son-declenche-automatiquement) par l’utilisateur ?"},"headers":[],"relativePath":"rgaa/criteres/4-multimedia/4.10/index.md","filePath":"rgaa/criteres/4-multimedia/4.10/index.md"}'),o={name:"rgaa/criteres/4-multimedia/4.10/index.md"};function n(i,e,s,c,u,d){return r(),a("div",null,[...e[0]||(e[0]=[l('<h1 id="chaque-son-declenche-automatiquement-est-il-controlable-par-l-utilisateur" tabindex="-1">Chaque son déclenché automatiquement est-il <a href="/eslint-plugin-rgaa/rgaa/glossaire/controle-son-declenche-automatiquement.html">contrôlable</a> par l’utilisateur ? <a class="header-anchor" href="#chaque-son-declenche-automatiquement-est-il-controlable-par-l-utilisateur" aria-label="Permalink to &quot;Chaque son déclenché automatiquement est-il [contrôlable](/rgaa/glossaire/controle-son-declenche-automatiquement) par l’utilisateur ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque séquence sonore déclenchée automatiquement via une balise <code>&lt;object&gt;</code>, <code>&lt;video&gt;</code>, <code>&lt;audio&gt;</code>, <code>&lt;embed&gt;</code>, <code>&lt;bgsound&gt;</code> ou un code JavaScript vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Au chargement du document, si un son se déclenche automatiquement, vérifier que : <ul><li>Soit la séquence sonore a une durée inférieure ou égale à 3 secondes ;</li><li>Soit un dispositif (un bouton par exemple), sur l’élément ayant déclenché le son (voir note), ou dans la page, permet de le stopper ;</li><li>Soit le volume de la séquence peut être contrôlé par l’utilisateur, indépendamment du contrôle de volume du système.</li></ul></li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><p>Note : les éléments suivants sont susceptibles de déclencher des sons au chargement de la page : éléments <code>&lt;audio&gt;</code>, <code>&lt;video&gt;</code>, <code>&lt;object&gt;</code>, <code>&lt;embed&gt;</code>, <code>&lt;bgsound&gt;</code> ou un code JavaScript (utilisation de la Web Audio API, par exemple).</p><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-4-2" tabindex="-1">Critère WCAG 1.4.2 <a class="header-anchor" href="#critere-wcag-1-4-2" aria-label="Permalink to &quot;Critère WCAG 1.4.2&quot;">​</a></h3><p><strong>Audio Control</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G60</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G60" target="_blank" rel="noreferrer">Technique G60</a></li><li><strong>G170</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G170" target="_blank" rel="noreferrer">Technique G170</a></li><li><strong>G171</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G171" target="_blank" rel="noreferrer">Technique G171</a></li><li><strong>F23</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F23" target="_blank" rel="noreferrer">Technique F23</a></li><li><strong>F93</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F93" target="_blank" rel="noreferrer">Technique F93</a></li></ul>',12)])])}const g=t(o,[["render",n]]);export{m as __pageData,g as default};
