import{_ as a,c as t,o as s,ag as i}from"./chunks/framework.BSOECjnK.js";const d=JSON.parse('{"title":"Chaque média temporel et non temporel est-il compatible avec les technologies d’assistance (hors cas particuliers) ?","description":"","frontmatter":{"title":"Chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) et [non temporel](/rgaa/glossaire/media-non-temporel) est-il [compatible avec les technologies d’assistance](/rgaa/glossaire/compatible-avec-les-technologies-d-assistance) (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/4-multimedia/4.13/index.md","filePath":"rgaa/criteres/4-multimedia/4.13/index.md"}'),r={name:"rgaa/criteres/4-multimedia/4.13/index.md"};function l(o,e,n,c,m,h){return s(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="chaque-media-temporel-et-non-temporel-est-il-compatible-avec-les-technologies-d-assistance-hors-cas-particuliers" tabindex="-1">Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> et <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-non-temporel.html">non temporel</a> est-il <a href="/eslint-plugin-rgaa/rgaa/glossaire/compatible-avec-les-technologies-d-assistance.html">compatible avec les technologies d’assistance</a> (hors cas particuliers) ? <a class="header-anchor" href="#chaque-media-temporel-et-non-temporel-est-il-compatible-avec-les-technologies-d-assistance-hors-cas-particuliers" aria-label="Permalink to &quot;Chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) et [non temporel](/rgaa/glossaire/media-non-temporel) est-il [compatible avec les technologies d’assistance](/rgaa/glossaire/compatible-avec-les-technologies-d-assistance) (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> et <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-non-temporel.html">non temporel</a> vérifie-t-il une de ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les médias temporels et non temporels ;</li><li>Pour chaque média, vérifier que : <ul><li>Soit le nom, le rôle, la valeur, le paramétrage et les changements d’états des composants d’interfaces sont accessibles aux technologies d’assistance via une API d’accessibilité (par exemple, les zones mises à jour dynamiquement dans un lecteur vidéo sont correctement restituées) ;</li><li>Soit une alternative compatible avec une API d’accessibilité permet d’accéder aux mêmes fonctionnalités.</li></ul></li><li>Si c’est le cas pour chaque média temporel ou non temporel, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> et <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-non-temporel.html">non temporel</a> qui possède une alternative <a href="/eslint-plugin-rgaa/rgaa/glossaire/compatible-avec-les-technologies-d-assistance.html">compatible avec les technologies d’assistance</a>, vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les médias temporels et non temporels qui possèdent une alternative compatible avec les technologies d’assistance ;</li><li>Pour chaque média, vérifier que : <ul><li>Soit l’alternative est adjacente au média temporel ou non temporel ;</li><li>Soit l’alternative est accessible au moyen d’un lien ou d’un bouton adjacent ;</li><li>Soit un mécanisme permet de remplacer le média temporel ou non temporel par son alternative.</li></ul></li><li>Si c’est le cas pour chaque média temporel ou non temporel, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-4-1-2" tabindex="-1">Critère WCAG 4.1.2 <a class="header-anchor" href="#critere-wcag-4-1-2" aria-label="Permalink to &quot;Critère WCAG 4.1.2&quot;">​</a></h3><p><strong>Name, role, Value</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G10</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G10" target="_blank" rel="noreferrer">Technique G10</a></li><li><strong>G135</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G135" target="_blank" rel="noreferrer">Technique G135</a></li><li><strong>F15</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F15" target="_blank" rel="noreferrer">Technique F15</a></li><li><strong>F54</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F54" target="_blank" rel="noreferrer">Technique F54</a></li></ul>',14)])])}const p=a(r,[["render",l]]);export{d as __pageData,p as default};
