import{_ as r,c as t,o as i,ag as a}from"./chunks/framework.BSOECjnK.js";const p=JSON.parse('{"title":"Chaque média temporel pré-enregistré a-t-il, si nécessaire, une transcription textuelle ou une audiodescription (hors cas particuliers) ?","description":"","frontmatter":{"title":"Chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) pré-enregistré a-t-il, si nécessaire, une [transcription textuelle](/rgaa/glossaire/transcription-textuelle-media-temporel) ou une [audiodescription](/rgaa/glossaire/audiodescription-synchronisee-media-temporel) (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/4-multimedia/4.1/index.md","filePath":"rgaa/criteres/4-multimedia/4.1/index.md"}'),n={name:"rgaa/criteres/4-multimedia/4.1/index.md"};function s(o,e,l,u,c,d){return i(),t("div",null,[...e[0]||(e[0]=[a('<h1 id="chaque-media-temporel-pre-enregistre-a-t-il-si-necessaire-une-transcription-textuelle-ou-une-audiodescription-hors-cas-particuliers" tabindex="-1">Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> pré-enregistré a-t-il, si nécessaire, une <a href="/eslint-plugin-rgaa/rgaa/glossaire/transcription-textuelle-media-temporel.html">transcription textuelle</a> ou une <a href="/eslint-plugin-rgaa/rgaa/glossaire/audiodescription-synchronisee-media-temporel.html">audiodescription</a> (hors cas particuliers) ? <a class="header-anchor" href="#chaque-media-temporel-pre-enregistre-a-t-il-si-necessaire-une-transcription-textuelle-ou-une-audiodescription-hors-cas-particuliers" aria-label="Permalink to &quot;Chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) pré-enregistré a-t-il, si nécessaire, une [transcription textuelle](/rgaa/glossaire/transcription-textuelle-media-temporel) ou une [audiodescription](/rgaa/glossaire/audiodescription-synchronisee-media-temporel) (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> pré-enregistré seulement audio, vérifie-t-il, si nécessaire, l’une de ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les médias temporels (éléments <code>&lt;audio&gt;</code>, <code>&lt;video&gt;</code> ou <code>&lt;object&gt;</code>) seulement audio qui nécessitent une transcription textuelle ;</li><li>Pour chaque média temporel seulement audio, vérifier la présence d’une transcription textuelle : <ul><li>Soit accessible au moyen d’un bouton ou d’un lien adjacent (une URL ou une ancre) ;</li><li>Soit adjacente clairement identifiable.</li></ul></li><li>Si c’est le cas pour chaque média temporel, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> pré-enregistré seulement vidéo vérifie-t-il, si nécessaire, l’une de ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les médias temporels (éléments <code>&lt;video&gt;</code> ou <code>&lt;object&gt;</code>) seulement vidéo qui nécessitent une transcription textuelle ;</li><li>Pour chaque média temporel seulement vidéo, vérifier la présence : <ul><li>Soit d’une version alternative audio seulement accessible au moyen d’un lien ou bouton adjacent (une URL ou une ancre) ;</li><li>Soit d’une version alternative audio seulement adjacente ;</li><li>Soit d’une transcription textuelle accessible au moyen d’un bouton ou d’un lien adjacent (une URL ou une ancre) ;</li><li>Soit d’une transcription textuelle adjacente clairement identifiable ;</li><li>Soit d’une audiodescription synchronisée ;</li><li>Soit d’une version alternative avec une audiodescription synchronisée accessible au moyen d’un bouton ou d&#39;un lien adjacent (une URL ou une ancre).</li></ul></li><li>Si c’est le cas pour chaque média temporel, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> synchronisé pré-enregistré vérifie-t-il, si nécessaire, une de ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les médias temporels (éléments <code>&lt;video&gt;</code> ou <code>&lt;object&gt;</code>) synchronisés qui nécessitent une transcription textuelle ;</li><li>Pour chaque média temporel synchronisé, vérifier la présence : <ul><li>Soit d’une transcription textuelle accessible au moyen d’un lien ou bouton adjacent (une URL ou une ancre) ;</li><li>Soit d’une transcription textuelle adjacente clairement identifiable ;</li><li>Soit d’une audiodescription synchronisée ;</li><li>Soit d’une version alternative avec une audiodescription synchronisée accessible au moyen d’un bouton ou d’un lien adjacent (une URL ou une ancre).</li></ul></li><li>Si c’est le cas pour chaque média temporel, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-2-1" tabindex="-1">Critère WCAG 1.2.1 <a class="header-anchor" href="#critere-wcag-1-2-1" aria-label="Permalink to &quot;Critère WCAG 1.2.1&quot;">​</a></h3><p><strong>Audio-only and Video-only (Prerecorded)</strong> (Niveau A)</p><h3 id="critere-wcag-1-2-3" tabindex="-1">Critère WCAG 1.2.3 <a class="header-anchor" href="#critere-wcag-1-2-3" aria-label="Permalink to &quot;Critère WCAG 1.2.3&quot;">​</a></h3><p><strong>Audio Description or Media Alternative (Prerecorded)</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G58</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G58" target="_blank" rel="noreferrer">Technique G58</a></li><li><strong>G69</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G69" target="_blank" rel="noreferrer">Technique G69</a></li><li><strong>G78</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G78" target="_blank" rel="noreferrer">Technique G78</a></li><li><strong>G158</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G158" target="_blank" rel="noreferrer">Technique G158</a></li><li><strong>G159</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G159" target="_blank" rel="noreferrer">Technique G159</a></li><li><strong>G173</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G173" target="_blank" rel="noreferrer">Technique G173</a></li><li><strong>G8</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G8" target="_blank" rel="noreferrer">Technique G8</a></li><li><strong>G166</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G166" target="_blank" rel="noreferrer">Technique G166</a></li><li><strong>H96</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H96" target="_blank" rel="noreferrer">Technique H96</a></li><li><strong>SM6</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM6" target="_blank" rel="noreferrer">Technique SM6</a></li><li><strong>SM7</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM7" target="_blank" rel="noreferrer">Technique SM7</a></li></ul>',19)])])}const m=r(n,[["render",s]]);export{p as __pageData,m as default};
