import{_ as r,c as t,o as i,ag as a}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Pour chaque média temporel pré-enregistré ayant une transcription textuelle ou une audiodescription synchronisée, celles-ci sont-elles pertinentes (hors cas particuliers) ?","description":"","frontmatter":{"title":"Pour chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) pré-enregistré ayant une [transcription textuelle](/rgaa/glossaire/transcription-textuelle-media-temporel) ou une [audiodescription](/rgaa/glossaire/audiodescription-synchronisee-media-temporel) synchronisée, celles-ci sont-elles pertinentes (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/4-multimedia/4.2/index.md","filePath":"rgaa/criteres/4-multimedia/4.2/index.md"}'),s={name:"rgaa/criteres/4-multimedia/4.2/index.md"};function n(l,e,o,c,u,d){return i(),t("div",null,[...e[0]||(e[0]=[a('<h1 id="pour-chaque-media-temporel-pre-enregistre-ayant-une-transcription-textuelle-ou-une-audiodescription-synchronisee-celles-ci-sont-elles-pertinentes-hors-cas-particuliers" tabindex="-1">Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> pré-enregistré ayant une <a href="/eslint-plugin-rgaa/rgaa/glossaire/transcription-textuelle-media-temporel.html">transcription textuelle</a> ou une <a href="/eslint-plugin-rgaa/rgaa/glossaire/audiodescription-synchronisee-media-temporel.html">audiodescription</a> synchronisée, celles-ci sont-elles pertinentes (hors cas particuliers) ? <a class="header-anchor" href="#pour-chaque-media-temporel-pre-enregistre-ayant-une-transcription-textuelle-ou-une-audiodescription-synchronisee-celles-ci-sont-elles-pertinentes-hors-cas-particuliers" aria-label="Permalink to &quot;Pour chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) pré-enregistré ayant une [transcription textuelle](/rgaa/glossaire/transcription-textuelle-media-temporel) ou une [audiodescription](/rgaa/glossaire/audiodescription-synchronisee-media-temporel) synchronisée, celles-ci sont-elles pertinentes (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> pré-enregistré seulement audio, ayant une <a href="/eslint-plugin-rgaa/rgaa/glossaire/transcription-textuelle-media-temporel.html">transcription textuelle</a>, celle-ci est-elle pertinente (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les médias temporels pré-enregistrés seulement audio qui possèdent une transcription textuelle ;</li><li>Pour chaque média temporel seulement audio, vérifier que transcription textuelle est pertinente ;</li><li>Si c’est le cas pour chaque média temporel, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> pré-enregistré seulement vidéo vérifie-t-il une de ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les médias temporels pré-enregistrés seulement vidéo qui possèdent une transcription textuelle ;</li><li>Pour chaque média temporel seulement vidéo, vérifier la pertinence : <ul><li>Soit de la transcription textuelle ;</li><li>Soit de l’audiodescription synchronisée ;</li><li>Soit de l’audiodescription synchronisée de la version alternative ;</li><li>Soit de la version alternative audio seulement.</li></ul></li><li>Si c’est le cas pour chaque média temporel, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> synchronisé pré-enregistré vérifie-t-il une de ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les médias temporels pré-enregistrés synchronisés ;</li><li>Pour chaque média temporel synchronisé, vérifier la pertinence : <ul><li>Soit de la transcription textuelle ;</li><li>Soit de l’audiodescription synchronisée ;</li><li>Soit de l’audiodescription synchronisée de la version alternative.</li></ul></li><li>Si c’est le cas pour chaque média temporel, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-2-1" tabindex="-1">Critère WCAG 1.2.1 <a class="header-anchor" href="#critere-wcag-1-2-1" aria-label="Permalink to &quot;Critère WCAG 1.2.1&quot;">​</a></h3><p><strong>Audio-only and Video-only (Prerecorded)</strong> (Niveau A)</p><h3 id="critere-wcag-1-2-3" tabindex="-1">Critère WCAG 1.2.3 <a class="header-anchor" href="#critere-wcag-1-2-3" aria-label="Permalink to &quot;Critère WCAG 1.2.3&quot;">​</a></h3><p><strong>Audio Description or Media Alternative (Prerecorded)</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>F30</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F30" target="_blank" rel="noreferrer">Technique F30</a></li><li><strong>F67</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F67" target="_blank" rel="noreferrer">Technique F67</a></li><li><strong>SM6</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM6" target="_blank" rel="noreferrer">Technique SM6</a></li><li><strong>SM7</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM7" target="_blank" rel="noreferrer">Technique SM7</a></li></ul>',19)])])}const m=r(s,[["render",n]]);export{h as __pageData,m as default};
