import{_ as s,c as r,o as t,ag as i}from"./chunks/framework.BSOECjnK.js";const g=JSON.parse('{"title":"Chaque média temporel synchronisé pré-enregistré a-t-il, si nécessaire, des sous-titres synchronisés (hors cas particuliers) ?","description":"","frontmatter":{"title":"Chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) synchronisé pré-enregistré a-t-il, si nécessaire, des [sous-titres synchronisés](/rgaa/glossaire/sous-titres-synchronises-objet-multimedia) (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/4-multimedia/4.3/index.md","filePath":"rgaa/criteres/4-multimedia/4.3/index.md"}'),a={name:"rgaa/criteres/4-multimedia/4.3/index.md"};function n(o,e,l,c,h,u){return t(),r("div",null,[...e[0]||(e[0]=[i('<h1 id="chaque-media-temporel-synchronise-pre-enregistre-a-t-il-si-necessaire-des-sous-titres-synchronises-hors-cas-particuliers" tabindex="-1">Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> synchronisé pré-enregistré a-t-il, si nécessaire, des <a href="/eslint-plugin-rgaa/rgaa/glossaire/sous-titres-synchronises-objet-multimedia.html">sous-titres synchronisés</a> (hors cas particuliers) ? <a class="header-anchor" href="#chaque-media-temporel-synchronise-pre-enregistre-a-t-il-si-necessaire-des-sous-titres-synchronises-hors-cas-particuliers" aria-label="Permalink to &quot;Chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) synchronisé pré-enregistré a-t-il, si nécessaire, des [sous-titres synchronisés](/rgaa/glossaire/sous-titres-synchronises-objet-multimedia) (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> synchronisé pré-enregistré vérifie-t-il, si nécessaire, l’une de ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les médias temporels pré-enregistrés synchronisés ;</li><li>Pour chaque média temporel synchronisé, vérifier la présence : <ul><li>Soit de sous-titres synchronisés ;</li><li>Soit d’une version alternative possédant des sous-titres synchronisés accessible au moyen d’un lien ou d’un bouton adjacent.</li></ul></li><li>Si c’est le cas pour chaque média temporel, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> synchronisé pré-enregistré possédant des <a href="/eslint-plugin-rgaa/rgaa/glossaire/sous-titres-synchronises-objet-multimedia.html">sous-titres synchronisés</a> diffusés via une balise <code>&lt;track&gt;</code>, la balise <code>&lt;track&gt;</code> possède-t-elle un attribut <code>kind=&quot;captions&quot;</code> ?</strong></p><ol><li>Retrouver dans le document les médias temporels synchronisés possédant des sous-titres synchronisés au moyen d’un élément <code>&lt;track&gt;</code> ;</li><li>Pour chaque média temporel synchronisé, vérifier que la balise <code>&lt;track&gt;</code> possède un attribut <code>kind=&quot;caption&quot;</code> ;</li><li>Si c’est le cas pour chaque média temporel synchronisé, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-2-2" tabindex="-1">Critère WCAG 1.2.2 <a class="header-anchor" href="#critere-wcag-1-2-2" aria-label="Permalink to &quot;Critère WCAG 1.2.2&quot;">​</a></h3><p><strong>Captions (Prerecorded)</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G58</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G58" target="_blank" rel="noreferrer">Technique G58</a></li><li><strong>G93</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G93" target="_blank" rel="noreferrer">Technique G93</a></li><li><strong>G87</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G87" target="_blank" rel="noreferrer">Technique G87</a></li><li><strong>H95</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H95" target="_blank" rel="noreferrer">Technique H95</a></li><li><strong>SM11</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM11" target="_blank" rel="noreferrer">Technique SM11</a></li><li><strong>SM12</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM12" target="_blank" rel="noreferrer">Technique SM12</a></li><li><strong>F74</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F74" target="_blank" rel="noreferrer">Technique F74</a></li><li><strong>F75</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F75" target="_blank" rel="noreferrer">Technique F75</a></li></ul>',14)])])}const m=s(a,[["render",n]]);export{g as __pageData,m as default};
