import{_ as r,c as t,o as i,ag as a}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Pour chaque média temporel pré-enregistré ayant une audiodescription synchronisée, celle-ci est-elle pertinente ?","description":"","frontmatter":{"title":"Pour chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) pré-enregistré ayant une [audiodescription](/rgaa/glossaire/audiodescription-synchronisee-media-temporel) synchronisée, celle-ci est-elle pertinente ?"},"headers":[],"relativePath":"rgaa/criteres/4-multimedia/4.6/index.md","filePath":"rgaa/criteres/4-multimedia/4.6/index.md"}'),s={name:"rgaa/criteres/4-multimedia/4.6/index.md"};function n(o,e,l,c,d,u){return i(),t("div",null,[...e[0]||(e[0]=[a('<h1 id="pour-chaque-media-temporel-pre-enregistre-ayant-une-audiodescription-synchronisee-celle-ci-est-elle-pertinente" tabindex="-1">Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> pré-enregistré ayant une <a href="/eslint-plugin-rgaa/rgaa/glossaire/audiodescription-synchronisee-media-temporel.html">audiodescription</a> synchronisée, celle-ci est-elle pertinente ? <a class="header-anchor" href="#pour-chaque-media-temporel-pre-enregistre-ayant-une-audiodescription-synchronisee-celle-ci-est-elle-pertinente" aria-label="Permalink to &quot;Pour chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) pré-enregistré ayant une [audiodescription](/rgaa/glossaire/audiodescription-synchronisee-media-temporel) synchronisée, celle-ci est-elle pertinente ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> pré-enregistré seulement vidéo ayant une <a href="/eslint-plugin-rgaa/rgaa/glossaire/audiodescription-synchronisee-media-temporel.html">audiodescription</a> synchronisée, celle-ci est-elle pertinente ?</strong></p><ol><li>Retrouver dans le document les médias temporels seulement vidéo qui possèdent une audiodescription ;</li><li>Pour chaque média temporel, vérifier que l’audiodescription synchronisée est pertinente (toutes les informations visuelles qu’il est possible de vocaliser dans les blancs de la bande son principale sont présentes, les textes incrustés notamment) ;</li><li>Si c’est le cas pour chaque média temporel seulement vidéo, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> synchronisé ayant une <a href="/eslint-plugin-rgaa/rgaa/glossaire/audiodescription-synchronisee-media-temporel.html">audiodescription</a> synchronisée, celle-ci est-elle pertinente ?</strong></p><ol><li>Retrouver dans le document les médias temporels synchronisés qui possèdent une audiodescription ;</li><li>Pour chaque média temporel, vérifier que l’audiodescription synchronisée est pertinente (toutes les informations visuelles qu’il est possible de vocaliser dans les blancs de la bande son principale sont présentes, les textes incrustés notamment) ;</li><li>Si c’est le cas pour chaque média temporel synchronisé, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-2-5" tabindex="-1">Critère WCAG 1.2.5 <a class="header-anchor" href="#critere-wcag-1-2-5" aria-label="Permalink to &quot;Critère WCAG 1.2.5&quot;">​</a></h3><p><strong>Audio Description (Prerecorded)</strong> (Niveau AA)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>SM1</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM1" target="_blank" rel="noreferrer">Technique SM1</a></li><li><strong>SM2</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM2" target="_blank" rel="noreferrer">Technique SM2</a></li><li><strong>SM6</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM6" target="_blank" rel="noreferrer">Technique SM6</a></li><li><strong>SM7</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SM7" target="_blank" rel="noreferrer">Technique SM7</a></li></ul>',14)])])}const m=r(s,[["render",n]]);export{h as __pageData,m as default};
