import{_ as a,c as t,o as i,ag as r}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Chaque média temporel est-il clairement identifiable (hors cas particuliers) ?","description":"","frontmatter":{"title":"Chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) est-il clairement identifiable (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/4-multimedia/4.7/index.md","filePath":"rgaa/criteres/4-multimedia/4.7/index.md"}'),s={name:"rgaa/criteres/4-multimedia/4.7/index.md"};function l(n,e,o,c,d,u){return i(),t("div",null,[...e[0]||(e[0]=[r('<h1 id="chaque-media-temporel-est-il-clairement-identifiable-hors-cas-particuliers" tabindex="-1">Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> est-il clairement identifiable (hors cas particuliers) ? <a class="header-anchor" href="#chaque-media-temporel-est-il-clairement-identifiable-hors-cas-particuliers" aria-label="Permalink to &quot;Chaque [média temporel](/rgaa/glossaire/media-temporel-type-son-video-et-synchronise) est-il clairement identifiable (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> seulement son, seulement vidéo ou synchronisé, le contenu textuel adjacent permet-il d’identifier clairement le <a href="/eslint-plugin-rgaa/rgaa/glossaire/media-temporel-type-son-video-et-synchronise.html">média temporel</a> (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les médias temporels pré-enregistrés seulement vidéo, audio ou synchronisés ;</li><li>Pour chaque média temporel, vérifier que : <ul><li>Un passage de texte (un titre ou un paragraphe, par exemple) qui précède ou suit immédiatement le média temporel, permet de l’identifier ;</li><li>Et le passage de texte est situé à l’extérieur du lecteur de contenu multimédia si ce dernier fait appel à la technologie Flash.</li></ul></li><li>Si c’est le cas pour chaque média temporel, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-1-1" tabindex="-1">Critère WCAG 1.1.1 <a class="header-anchor" href="#critere-wcag-1-1-1" aria-label="Permalink to &quot;Critère WCAG 1.1.1&quot;">​</a></h3><p><strong>Non-text Content</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G68</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G68" target="_blank" rel="noreferrer">Technique G68</a></li><li><strong>G100</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G100" target="_blank" rel="noreferrer">Technique G100</a></li></ul>',11)])])}const p=a(s,[["render",l]]);export{h as __pageData,p as default};
