import{_ as t,c as a,o as l,ag as n}from"./chunks/framework.BSOECjnK.js";const g=JSON.parse('{"title":"Pour chaque tableau de données, chaque en-tête de colonne et chaque en-tête de ligne sont-ils correctement déclarés ?","description":"","frontmatter":{"title":"Pour chaque [tableau de données](/rgaa/glossaire/tableau-de-donnees), chaque [en-tête de colonne](/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne) et chaque [en-tête de ligne](/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne) sont-ils correctement déclarés ?"},"headers":[],"relativePath":"rgaa/criteres/5-tableaux/5.6/index.md","filePath":"rgaa/criteres/5-tableaux/5.6/index.md"}'),o={name:"rgaa/criteres/5-tableaux/5.6/index.md"};function r(s,e,i,u,d,c){return l(),a("div",null,[...e[0]||(e[0]=[n('<h1 id="pour-chaque-tableau-de-donnees-chaque-en-tete-de-colonne-et-chaque-en-tete-de-ligne-sont-ils-correctement-declares" tabindex="-1">Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/tableau-de-donnees.html">tableau de données</a>, chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne.html">en-tête de colonne</a> et chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne.html">en-tête de ligne</a> sont-ils correctement déclarés ? <a class="header-anchor" href="#pour-chaque-tableau-de-donnees-chaque-en-tete-de-colonne-et-chaque-en-tete-de-ligne-sont-ils-correctement-declares" aria-label="Permalink to &quot;Pour chaque [tableau de données](/rgaa/glossaire/tableau-de-donnees), chaque [en-tête de colonne](/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne) et chaque [en-tête de ligne](/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne) sont-ils correctement déclarés ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/tableau-de-donnees.html">tableau de données</a>, chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne.html">en-tête de colonne</a> s’appliquant à la totalité de la colonne vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les tableaux de données ;</li><li>Pour chaque en-tête de colonnes s’appliquant à la totalité de la colonne, vérifier que l’en-tête de colonne est structuré au moyen : <ul><li>Soit d’un élément <code>&lt;th&gt;</code> ;</li><li>Soit d’un élément pourvu d’un attribut WAI-ARIA <code>role=&quot;columnheader&quot;</code>.</li></ul></li><li>Si c’est le cas pour chaque en-tête de colonne s’appliquant à la totalité de la colonne, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/tableau-de-donnees.html">tableau de données</a>, chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne.html">en-tête de ligne</a> s’appliquant à la totalité de la ligne vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les tableaux de données ;</li><li>Pour chaque en-tête de ligne s’appliquant à la totalité de la ligne, vérifier que l’en-tête de ligne est structuré au moyen : <ul><li>Soit d’un élément <code>&lt;th&gt;</code> ;</li><li>Soit d’un élément pourvu d’un attribut WAI-ARIA <code>role=&quot;rowheader&quot;</code>.</li></ul></li><li>Si c’est le cas pour chaque en-tête de ligne s’appliquant à la totalité de la ligne, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/tableau-de-donnees.html">tableau de données</a>, chaque en-tête ne s’appliquant pas à la totalité de la ligne ou de la colonne est-il structuré au moyen d’une balise <code>&lt;th&gt;</code> ?</strong></p><ol><li>Retrouver dans le document les tableaux de données ;</li><li>Pour chaque en-tête ne s’appliquant pas à la totalité de la ligne ou de la colonne, vérifier que l’en-tête de ligne est structuré au moyen d’un élément <code>&lt;th&gt;</code> ;</li><li>Si c’est le cas pour chaque en-tête ne s’appliquant pas à la totalité de la ligne ou de la colonne, <strong>le test est validé</strong>.</li></ol><h3 id="test-4" tabindex="-1">Test 4 <a class="header-anchor" href="#test-4" aria-label="Permalink to &quot;Test 4&quot;">​</a></h3><p><strong>Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/tableau-de-donnees.html">tableau de données</a>, chaque cellule associée à plusieurs en-têtes est-elle structurée au moyen d’une balise <code>&lt;td&gt;</code> ou <code>&lt;th&gt;</code> ?</strong></p><ol><li>Retrouver dans le document les tableaux de données ;</li><li>Pour chaque cellule associée à plusieurs en-têtes est-elle structurée au moyen d’une balise <code>&lt;th&gt;</code> ou <code>&lt;td&gt;</code> ;</li><li>Si c’est le cas pour chaque en-tête ne s’appliquant pas à la totalité de la ligne ou de la colonne, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-3-1" tabindex="-1">Critère WCAG 1.3.1 <a class="header-anchor" href="#critere-wcag-1-3-1" aria-label="Permalink to &quot;Critère WCAG 1.3.1&quot;">​</a></h3><p><strong>Info and Relationships</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>H51</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H51" target="_blank" rel="noreferrer">Technique H51</a></li><li><strong>F91</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F91" target="_blank" rel="noreferrer">Technique F91</a></li></ul>',20)])])}const q=t(o,[["render",r]]);export{g as __pageData,q as default};
