import{_ as t,c as o,o as a,ag as l}from"./chunks/framework.BSOECjnK.js";const p=JSON.parse('{"title":"Pour chaque tableau de données, la technique appropriée permettant d’associer chaque cellule avec ses en-têtes est-elle utilisée (hors cas particuliers) ?","description":"","frontmatter":{"title":"Pour chaque [tableau de données](/rgaa/glossaire/tableau-de-donnees), la technique appropriée permettant d’associer chaque cellule avec ses [en-têtes](/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne) est-elle utilisée (hors cas particuliers) ?"},"headers":[],"relativePath":"rgaa/criteres/5-tableaux/5.7/index.md","filePath":"rgaa/criteres/5-tableaux/5.7/index.md"}'),s={name:"rgaa/criteres/5-tableaux/5.7/index.md"};function i(r,e,n,u,c,d){return a(),o("div",null,[...e[0]||(e[0]=[l('<h1 id="pour-chaque-tableau-de-donnees-la-technique-appropriee-permettant-d-associer-chaque-cellule-avec-ses-en-tetes-est-elle-utilisee-hors-cas-particuliers" tabindex="-1">Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/tableau-de-donnees.html">tableau de données</a>, la technique appropriée permettant d’associer chaque cellule avec ses <a href="/eslint-plugin-rgaa/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne.html">en-têtes</a> est-elle utilisée (hors cas particuliers) ? <a class="header-anchor" href="#pour-chaque-tableau-de-donnees-la-technique-appropriee-permettant-d-associer-chaque-cellule-avec-ses-en-tetes-est-elle-utilisee-hors-cas-particuliers" aria-label="Permalink to &quot;Pour chaque [tableau de données](/rgaa/glossaire/tableau-de-donnees), la technique appropriée permettant d’associer chaque cellule avec ses [en-têtes](/rgaa/glossaire/en-tete-de-colonne-ou-de-ligne) est-elle utilisée (hors cas particuliers) ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Pour chaque contenu de balise <code>&lt;th&gt;</code> s’appliquant à la totalité de la ligne ou de la colonne, la balise <code>&lt;th&gt;</code> respecte-t-elle une de ces conditions (hors cas particuliers) ?</strong></p><ol><li>Retrouver dans le document les tableaux de données ;</li><li>Pour chaque en-tête (élément <code>&lt;th&gt;</code>) s’appliquant à la totalité de la ligne ou de la colonne, vérifier que l’élément <code>&lt;th&gt;</code> possède : <ul><li>Soit un attribut <code>id</code> unique ;</li><li>Soit un attribut scope ;</li><li>Soit un attribut WAI-ARIA <code>role=&quot;rowheader&quot;</code> ou <code>&quot;columnheader&quot;</code>.</li></ul></li><li>Si c’est le cas pour chaque en-tête s’appliquant à la totalité de la ligne ou de la colonne, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Pour chaque contenu de balise <code>&lt;th&gt;</code> s’appliquant à la totalité de la ligne ou de la colonne et possédant un attribut <code>scope</code>, la balise <code>&lt;th&gt;</code> vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les tableaux de données ;</li><li>Pour chaque en-tête (élément <code>&lt;th&gt;</code>) s’appliquant à la totalité de la ligne ou de la colonne et pourvu d’un attribut <code>scope</code>, vérifier que l’attribut <code>scope</code> possède : <ul><li>Soit une valeur <code>&quot;row&quot;</code> pour les en-têtes de ligne ;</li><li>Soit une valeur <code>&quot;col&quot;</code> pour les en-têtes de colonne.</li></ul></li><li>Si c’est le cas pour chaque en-tête s’appliquant à la totalité de la ligne ou de la colonne et pourvu d’un attribut <code>scope</code>, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Pour chaque contenu de balise <code>&lt;th&gt;</code> ne s’appliquant pas à la totalité de la ligne ou de la colonne, la balise <code>&lt;th&gt;</code> vérifie-t-elle ces conditions ?</strong></p><ol><li>Retrouver dans le document les tableaux de données ;</li><li>Pour chaque en-tête (élément <code>&lt;th&gt;</code>) ne s’appliquant pas à la totalité de la ligne ou de la colonne, vérifier que l’élément <code>&lt;th&gt;</code> : <ul><li>Possède un attribut <code>id</code> unique ;</li><li>Et ne possède pas d’attribut <code>scope </code>;</li><li>Et ne possède pas d’attribut WAI-ARIA <code>role=&quot;rowheader&quot;</code> ou <code>&quot;columnheader&quot;</code>.</li></ul></li><li>Si c’est le cas pour chaque en-tête ne s’appliquant pas à la totalité de la ligne ou de la colonne, <strong>le test est validé</strong>.</li></ol><h3 id="test-4" tabindex="-1">Test 4 <a class="header-anchor" href="#test-4" aria-label="Permalink to &quot;Test 4&quot;">​</a></h3><p><strong>Pour chaque contenu de balise <code>&lt;td&gt;</code> ou <code>&lt;th&gt;</code> associée à un ou plusieurs en-têtes possédant un attribut <code>id</code>, la balise vérifie-t-elle ces conditions ?</strong></p><ol><li>Retrouver dans le document les tableaux de données ;</li><li>Pour chaque élément <code>&lt;td&gt;</code> ou <code>&lt;th&gt;</code> associé à un ou plusieurs en-têtes possédant un attribut <code>id</code>, vérifier que : <ul><li>L’élément <code>&lt;td&gt;</code> ou <code>&lt;th&gt;</code> possède un attribut <code>headers</code> ;</li><li>Et l’attribut <code>headers</code> possède la liste des valeurs d’attribut <code>id</code> des en-têtes associés.</li></ul></li><li>Si c’est le cas pour chaque élément <code>&lt;td&gt;</code> ou <code>&lt;th&gt;</code> associé à un ou plusieurs en-têtes possédant un attribut <code>id</code>, <strong>le test est validé</strong>.</li></ol><h3 id="test-5" tabindex="-1">Test 5 <a class="header-anchor" href="#test-5" aria-label="Permalink to &quot;Test 5&quot;">​</a></h3><p><strong>Pour chaque balise pourvue d’un attribut WAI-ARIA <code>role=&quot;rowheader&quot;</code> ou <code>role=&quot;columnheader&quot;</code> dont le contenu s’applique à la totalité de la ligne ou de la colonne, la balise vérifie-t-elle une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les tableaux de données ;</li><li>Pour chaque en-tête s’appliquant à la totalité de la ligne ou de la colonne et pourvu d’un attribut WAI-ARIA <code>role=&quot;rowheader&quot;</code> ou <code>&quot;columnheader&quot;</code>, vérifier que l’élément possède : <ul><li>Soit un attribut WAI-ARIA <code>role=&quot;rowheader&quot;</code> pour les en-têtes de ligne ;</li><li>Soit un attribut WAI-ARIA <code>role=&quot;columnheader&quot;</code> pour les en-têtes de colonne.</li></ul></li><li>Si c’est le cas pour chaque en-tête s’appliquant à la totalité de la ligne ou de la colonne et pourvu d’un attribut WAI-ARIA <code>role=&quot;rowheader&quot;</code> ou <code>&quot;columnheader&quot;</code>, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-3-1" tabindex="-1">Critère WCAG 1.3.1 <a class="header-anchor" href="#critere-wcag-1-3-1" aria-label="Permalink to &quot;Critère WCAG 1.3.1&quot;">​</a></h3><p><strong>Info and Relationships</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>H43</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H43" target="_blank" rel="noreferrer">Technique H43</a></li><li><strong>H63</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H63" target="_blank" rel="noreferrer">Technique H63</a></li><li><strong>F90</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F90" target="_blank" rel="noreferrer">Technique F90</a></li></ul>',23)])])}const q=t(s,[["render",i]]);export{p as __pageData,q as default};
