import{_ as t,c as r,o as i,ag as a}from"./chunks/framework.BSOECjnK.js";const d=JSON.parse('{"title":"Pour chaque script qui initie un changement de contexte, l’utilisateur est-il averti ou en a-t-il le contrôle ?","description":"","frontmatter":{"title":"Pour chaque [script](/rgaa/glossaire/script) qui initie un [changement de contexte](/rgaa/glossaire/changement-de-contexte), l’utilisateur est-il averti ou en a-t-il le contrôle ?"},"headers":[],"relativePath":"rgaa/criteres/7-scripts/7.4/index.md","filePath":"rgaa/criteres/7-scripts/7.4/index.md"}'),n={name:"rgaa/criteres/7-scripts/7.4/index.md"};function l(s,e,o,u,c,h){return i(),r("div",null,[...e[0]||(e[0]=[a('<h1 id="pour-chaque-script-qui-initie-un-changement-de-contexte-l-utilisateur-est-il-averti-ou-en-a-t-il-le-controle" tabindex="-1">Pour chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/script.html">script</a> qui initie un <a href="/eslint-plugin-rgaa/rgaa/glossaire/changement-de-contexte.html">changement de contexte</a>, l’utilisateur est-il averti ou en a-t-il le contrôle ? <a class="header-anchor" href="#pour-chaque-script-qui-initie-un-changement-de-contexte-l-utilisateur-est-il-averti-ou-en-a-t-il-le-controle" aria-label="Permalink to &quot;Pour chaque [script](/rgaa/glossaire/script) qui initie un [changement de contexte](/rgaa/glossaire/changement-de-contexte), l’utilisateur est-il averti ou en a-t-il le contrôle ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/script.html">script</a> qui initie un <a href="/eslint-plugin-rgaa/rgaa/glossaire/changement-de-contexte.html">changement de contexte</a> vérifie-t-il une de ces conditions ?</strong></p><ol><li>Retrouver dans le document tous les événements JavaScript qui initient un changement de contexte, par exemple : <ul><li>Une mise à jour dynamique de champs de formulaire ;</li><li>L’ouverture d’une nouvelle page à l’activation d’une option d’une liste de sélection (élément <code>&lt;select&gt;</code>) ;</li><li>La mise à jour, via un procédé AJAX d’une partie essentielle de la page ;</li><li>Le lancement automatique d’un lecteur vidéo suite à la sélection d’une playlist ;</li><li>La manipulation du focus ayant pour résultat de modifier la position courante de l’utilisateur dans la page.</li></ul></li><li>Vérifier que : <ul><li>L’utilisateur est averti par un message de l’action du script et du type de changement avant son déclenchement ;</li><li>Ou bien le changement de contexte est initié par un bouton (input de type submit, button ou image ou la balise button) explicite ;</li><li>Ou bien le changement de contexte est initié par un lien explicite.</li></ul></li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-3-2-1" tabindex="-1">Critère WCAG 3.2.1 <a class="header-anchor" href="#critere-wcag-3-2-1" aria-label="Permalink to &quot;Critère WCAG 3.2.1&quot;">​</a></h3><p><strong>On Focus</strong> (Niveau A)</p><h3 id="critere-wcag-3-2-2" tabindex="-1">Critère WCAG 3.2.2 <a class="header-anchor" href="#critere-wcag-3-2-2" aria-label="Permalink to &quot;Critère WCAG 3.2.2&quot;">​</a></h3><p><strong>On Input</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G13</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G13" target="_blank" rel="noreferrer">Technique G13</a></li><li><strong>G76</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G76" target="_blank" rel="noreferrer">Technique G76</a></li><li><strong>G80</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G80" target="_blank" rel="noreferrer">Technique G80</a></li><li><strong>G107</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G107" target="_blank" rel="noreferrer">Technique G107</a></li><li><strong>H32</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H32" target="_blank" rel="noreferrer">Technique H32</a></li><li><strong>H84</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H84" target="_blank" rel="noreferrer">Technique H84</a></li><li><strong>F9</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F9" target="_blank" rel="noreferrer">Technique F9</a></li><li><strong>F22</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F22" target="_blank" rel="noreferrer">Technique F22</a></li><li><strong>F36</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F36" target="_blank" rel="noreferrer">Technique F36</a></li><li><strong>F37</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F37" target="_blank" rel="noreferrer">Technique F37</a></li><li><strong>F41</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F41" target="_blank" rel="noreferrer">Technique F41</a></li><li><strong>SCR19</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/SCR19" target="_blank" rel="noreferrer">Technique SCR19</a></li></ul>',13)])])}const p=t(n,[["render",l]]);export{d as __pageData,p as default};
