import{_ as t,c as s,o as a,ag as i}from"./chunks/framework.BSOECjnK.js";const h=JSON.parse('{"title":"Dans chaque page web, les messages de statut sont-ils correctement restitués par les technologies d’assistance ?","description":"","frontmatter":{"title":"Dans chaque page web, les [messages de statut](/rgaa/glossaire/message-de-statut) sont-ils correctement restitués par les technologies d’assistance ?"},"headers":[],"relativePath":"rgaa/criteres/7-scripts/7.5/index.md","filePath":"rgaa/criteres/7-scripts/7.5/index.md"}'),r={name:"rgaa/criteres/7-scripts/7.5/index.md"};function l(o,e,n,u,c,d){return a(),s("div",null,[...e[0]||(e[0]=[i('<h1 id="dans-chaque-page-web-les-messages-de-statut-sont-ils-correctement-restitues-par-les-technologies-d-assistance" tabindex="-1">Dans chaque page web, les <a href="/eslint-plugin-rgaa/rgaa/glossaire/message-de-statut.html">messages de statut</a> sont-ils correctement restitués par les technologies d’assistance ? <a class="header-anchor" href="#dans-chaque-page-web-les-messages-de-statut-sont-ils-correctement-restitues-par-les-technologies-d-assistance" aria-label="Permalink to &quot;Dans chaque page web, les [messages de statut](/rgaa/glossaire/message-de-statut) sont-ils correctement restitués par les technologies d’assistance ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/message-de-statut.html">message de statut</a> qui informe de la réussite, du résultat d’une action ou bien de l’état d’une application utilise-t-il l’attribut WAI-ARIA <code>role=&quot;status&quot;</code> ?</strong></p><ol><li>Retrouver dans le document les messages qui valent pour message de statut.</li><li>Pour chacun de ces messages, déterminer la nature de l’information dont est porteur le message :</li><li>Si le message informe de la réussite, du résultat d’une action ou bien de l’état d’une application, vérifier que l’élément qui contient le message : <ul><li>Soit utilise l’attribut WAI-ARIA <code>role=”status”</code> ;</li><li>Soit utilise les attributs WAI-ARIA <code>aria-live=”polite”</code> et <code>aria-atomic=”true”</code>.</li></ul></li><li>Si le message présente une suggestion, ou avertit de l’existence d’une erreur, vérifier que l’élément qui contient le message : <ul><li>Soit utilise l’attribut WAI-ARIA <code>role=”alert”</code> ;</li><li>Soit utilise les attributs <code>aria-live=”assertive”</code> et <code>aria-atomic=”true”</code>.</li></ul></li><li>Si le message indique la progression d’un processus, vérifier que l’élément qui contient le message : <ul><li>Soit utilise l’un des attributs WAI-ARIA <code>role=”log”</code>, <code>role=”progressbar”</code> ou <code>role=”status”</code> ;</li><li>Soit utilise l’attribut WAI-ARIA <code>aria-live=”polite”</code> si l’intention est de signaler l’équivalent d’un <code>rôle “log”</code> ;</li><li>Soit utilise les attributs WAI-ARIA <code>aria-live=”polite”</code> et aria-atomic=”true si l’intention est de signaler l’équivalent d’un rôle “status”.</li></ul></li><li>Si c’est le cas, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/message-de-statut.html">message de statut</a> qui présente une suggestion, ou avertit de l’existence d’une erreur utilise-t-il l’attribut WAI-ARIA <code>role=&quot;alert&quot;</code> ?</strong></p><p>Tests identiques à 7.5.1</p><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/message-de-statut.html">message de statut</a> qui indique la progression d’un processus utilise-t-il l’un des attributs WAI-ARIA <code>role=&quot;log&quot;</code>, <code>role=&quot;progressbar&quot;</code> ou <code>role=&quot;status&quot;</code> ?</strong></p><p>Tests identiques à 7.5.1</p><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-4-1-3" tabindex="-1">Critère WCAG 4.1.3 <a class="header-anchor" href="#critere-wcag-4-1-3" aria-label="Permalink to &quot;Critère WCAG 4.1.3&quot;">​</a></h3><p><strong>Status Messages</strong> (Niveau AA)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>ARIA19</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/ARIA19" target="_blank" rel="noreferrer">Technique ARIA19</a></li><li><strong>ARIA22</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/ARIA22" target="_blank" rel="noreferrer">Technique ARIA22</a></li><li><strong>ARIA23</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/ARIA23" target="_blank" rel="noreferrer">Technique ARIA23</a></li></ul>',17)])])}const m=t(r,[["render",l]]);export{h as __pageData,m as default};
