import{_ as t,c as r,o as s,ag as l}from"./chunks/framework.BSOECjnK.js";const g=JSON.parse('{"title":"Dans chaque page web, chaque liste est-elle correctement structurée ?","description":"","frontmatter":{"title":"Dans chaque page web, chaque [liste](/rgaa/glossaire/listes) est-elle correctement structurée ?"},"headers":[],"relativePath":"rgaa/criteres/9-structure/9.3/index.md","filePath":"rgaa/criteres/9-structure/9.3/index.md"}'),a={name:"rgaa/criteres/9-structure/9.3/index.md"};function o(i,e,n,u,c,d){return s(),r("div",null,[...e[0]||(e[0]=[l('<h1 id="dans-chaque-page-web-chaque-liste-est-elle-correctement-structuree" tabindex="-1">Dans chaque page web, chaque <a href="/eslint-plugin-rgaa/rgaa/glossaire/listes.html">liste</a> est-elle correctement structurée ? <a class="header-anchor" href="#dans-chaque-page-web-chaque-liste-est-elle-correctement-structuree" aria-label="Permalink to &quot;Dans chaque page web, chaque [liste](/rgaa/glossaire/listes) est-elle correctement structurée ?&quot;">​</a></h1><h2 id="tests" tabindex="-1">Tests <a class="header-anchor" href="#tests" aria-label="Permalink to &quot;Tests&quot;">​</a></h2><h3 id="test-1" tabindex="-1">Test 1 <a class="header-anchor" href="#test-1" aria-label="Permalink to &quot;Test 1&quot;">​</a></h3><p><strong>Dans chaque page web, les informations regroupées visuellement sous forme de <a href="/eslint-plugin-rgaa/rgaa/glossaire/listes.html">liste</a> non ordonnée vérifient-elles une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments regroupés visuellement sous la forme d’une liste non ordonnée ;</li><li>Pour chaque liste, vérifier que la liste est structurée : <ul><li>Soit au moyen des éléments <code>&lt;ul&gt;</code> et <code>&lt;li&gt;</code> ;</li><li>Soit au moyen d’éléments pourvus d’attributs WAI-ARIA <code>role=&quot;list&quot;</code> et <code>role=&quot;listitem&quot;</code>.</li></ul></li><li>Si c’est le cas pour chaque liste non ordonnée, <strong>le test est validé</strong>.</li></ol><h3 id="test-2" tabindex="-1">Test 2 <a class="header-anchor" href="#test-2" aria-label="Permalink to &quot;Test 2&quot;">​</a></h3><p><strong>Dans chaque page web, les informations regroupées visuellement sous forme de <a href="/eslint-plugin-rgaa/rgaa/glossaire/listes.html">liste</a> ordonnée vérifient-elles une de ces conditions ?</strong></p><ol><li>Retrouver dans le document les éléments regroupés visuellement sous la forme d’une liste ordonnée ;</li><li>Pour chaque liste, vérifier que la liste est structurée : <ul><li>Soit au moyen des éléments <code>&lt;ol&gt;</code> et <code>&lt;li&gt;</code> ;</li><li>Soit au moyen d’éléments pourvus d’attributs WAI-ARIA <code>role=&quot;list&quot;</code> et <code>role=&quot;listitem&quot;</code>.</li></ul></li><li>Si c’est le cas pour chaque liste ordonnée, <strong>le test est validé</strong>.</li></ol><h3 id="test-3" tabindex="-1">Test 3 <a class="header-anchor" href="#test-3" aria-label="Permalink to &quot;Test 3&quot;">​</a></h3><p><strong>Dans chaque page web, les informations regroupées sous forme de <a href="/eslint-plugin-rgaa/rgaa/glossaire/listes.html">liste</a> de description utilisent-elles les balises <code>&lt;dl&gt;</code> et <code>&lt;dt&gt;/&lt;dd&gt;</code> ?</strong></p><ol><li>Retrouver dans le document les éléments regroupés visuellement sous la forme d’une liste de description ;</li><li>Pour chaque liste, vérifier que la liste est structurée au moyen des éléments <code>&lt;dl&gt;</code>, <code>&lt;dt&gt;</code> et <code>&lt;dd&gt;</code> ;</li><li>Si c’est le cas pour chaque liste de description, <strong>le test est validé</strong>.</li></ol><h2 id="references-wcag" tabindex="-1">Références WCAG <a class="header-anchor" href="#references-wcag" aria-label="Permalink to &quot;Références WCAG&quot;">​</a></h2><h3 id="critere-wcag-1-3-1" tabindex="-1">Critère WCAG 1.3.1 <a class="header-anchor" href="#critere-wcag-1-3-1" aria-label="Permalink to &quot;Critère WCAG 1.3.1&quot;">​</a></h3><p><strong>Info and Relationships</strong> (Niveau A)</p><h2 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h2><p>Les techniques suivantes peuvent être utilisées pour implémenter ce critère :</p><ul><li><strong>G115</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G115" target="_blank" rel="noreferrer">Technique G115</a></li><li><strong>G153</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/G153" target="_blank" rel="noreferrer">Technique G153</a></li><li><strong>H40</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H40" target="_blank" rel="noreferrer">Technique H40</a></li><li><strong>H48</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/H48" target="_blank" rel="noreferrer">Technique H48</a></li><li><strong>F2</strong> : <a href="https://www.w3.org/WAI/WCAG21/Techniques/html/F2" target="_blank" rel="noreferrer">Technique F2</a></li></ul>',17)])])}const m=t(a,[["render",o]]);export{g as __pageData,m as default};
